import "./style.css";
import React, { useState } from 'react';
import {
  Layout,
  Card,
  Input,
  Col,
  Row,
  Select,
  Table,
  Button,
  Typography,
} from "antd";

import { HighlightOutlined, SmileOutlined, SmileFilled } from '@ant-design/icons';
const { Content } = Layout;
const { Title, Paragraph } = Typography;
const { Option } = Select;
const columns = [
  {
    title: "Mã Chi tiết HĐ",
    dataIndex: "MaCTHD",
    key: "MaCTHD",
  },
  {
    title: "Tên Sản Phẩm",
    dataIndex: "TenSP",
    key: "TenSP",
    render: (text) => <a>{text}</a>,
  },
  {
    title: "Số lượng",
    dataIndex: "SoLug",
    key: "SoLug",
  },
  {
    title: "Giá Tiền",
    dataIndex: "GiaTien",
    key: "GiaTien",
  },
];

const data = [
  {
    MaCTHD: "1",
    TenSP: "John Brown",
    SoLug: 32,
    GiaTien: "100000",
  },
  {
    MaCTHD: "2",
    TenSP: "Jim Green",
    SoLug: 42,
    GiaTien: "100000",
  },
  {
    MaCTHD: "3",
    TenSP: "Joe Black",
    SoLug: 32,
    GiaTien: "100000",
  },
];
function handleChange(value) {
  console.log(`selected ${value}`);
}

const ThongTinHD = () => {
  return (
    <Layout>
      <Content>
        <div>
            <div>
                <Title level={2}>Thông Tin Nhà Bán</Title>
            </div>
            <div>
                
            </div>
        </div>
      </Content>
    </Layout>
  );
};
export default ThongTinHD;
