/* eslint-disable jsx-a11y/anchor-is-valid */
import { Table, InputNumber } from "antd";
import { DeleteOutlined } from "@ant-design/icons";
import { useEffect } from "react";

const TableItem = ({ bill, setBill }) => {

  function onChange(value, record) {
    record = {
      ...record,
      SoLuong: value,
      ThanhTien: record.DonGia * value,
    };
    let temp = bill;
    for (let i = 0; i < temp.length; i++) {
      if (temp[i].key === record.key) {
        temp[i] = record;
      }
    }
    setBill(temp);
    const cart = bill.map((item) => {
      return {
        Ten: item.Ten,
        SoLuong: item.SoLuong,
        DonGia: item.DonGia,
        ThanhTien: item.ThanhTien,
      };
    });
    window.localStorage.setItem("cart", JSON.stringify(cart));
  }

  const onClickDelete = (record) => {
    console.log(record);
  }
  
  const columns = [
    {
      title: "Tên sản phẩm",
      dataIndex: "Ten",
      render: (text) => <a>{text}</a>,
    },
    {
      title: "Đơn giá",
      dataIndex: "DonGia",
      render: (text) => <div>{text}.000 Đồng</div>,
    },
    {
      title: "Số lượng",
      dataIndex: "SoLuong",
      render: (text, record) => (
        <InputNumber
          min={0}
          max={10}
          defaultValue={text}
          onChange={(value) => onChange(value, record)}
        />
      ),
    },
    {
      title: "Thành tiền",
      dataIndex: "ThanhTien",
      render: (text) => <div>{text}.000 Đồng</div>,
    },
    {
      title: () => <DeleteOutlined />,
      render: () => (
        <div>
          <DeleteOutlined />
        </div>
      ),
    },
  ];



  return (
    <div>
      <Table
       
        columns={columns}
        dataSource={bill}
      />
    </div>
  );
};

export default TableItem;
