import TableItem from "./cartComponents/TableItem";
import InfoPanel from "./cartComponents/InfoPanel";
import { Row, Col } from "antd";
import "./Cart.css";
import { useState, useEffect } from "react";
import axios from "axios";

const Cart = () => {
  const [cart, setCart] = useState([]);
  const [bill, setBill] = useState([]);

  const fetchBill = () => {
    let temp = [];
    if (window.localStorage.getItem("cart").length !== 0) {
      setCart(JSON.parse(window.localStorage.getItem("cart")));
    }
    for (let i = 0; i < cart.length; i++) {
      axios
        .get("http://localhost:5000/api/ctsp/" + cart[i].MaSP)
        .then((res) => {
          temp.push(res.data[0]);
          setBill(temp);
          console.log(bill);
        })
        .catch((err) => {
          console.log(err);
        });
    }
  };

  useEffect(() => {
    fetchBill();
    bill.map((item, pos) => {
      return {
        key: pos,
        nameItem: item.TenSP,
        price: price,
        amount: cart[pos].SoLuong,
        total: price * cart[pos].SoLuong,
      };
    })
  }, []);

  return (
    <div className="wrap-cart">
      <h3>Giỏ hàng</h3>
      <Row>
        <Col flex={10}>
          <TableItem bill={bill} />
        </Col>
        <Col flex={2}></Col>
        <Col flex="300px">
          <InfoPanel />
        </Col>
      </Row>
    </div>
  );
};

export default Cart;
