import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import React from "react";
import { render } from "react-dom";
import DangNhapGH from "./components/dangnhapgh";
import DanhSachDH from "./components/danhsachDH";
import ChiTietDH from "./components/chitietDH";

const NVGH = () => {
    const [auth, setAuth] = useState(window.localStorage.getItem("NVGH"));
    const [verify,setVerify] = useState(false)
  
    useEffect(() => {
      setAuth(window.localStorage.getItem("NV"));
    }, [verify]);
  
  
    return (
      <Router>
        <Switch>
          <Route exact path="/QT/LogIn">
            <LogIn auth={auth} setAuth={setAuth}  setVerify={setVerify} verify={verify} />
          </Route>
  
          <Route exact path="/QT">
            {auth === null ? (
              <Redirect to="/QT/LogIn" />
            ) : (
              <Main/>
            )}
          </Route>
        </Switch>
      </Router>
    );
  };
  
  export default NVQT;
