import { Column } from "@ant-design/charts";
import React, { useState, useEffect } from "react";
import axios from "axios";

const Chart = ({ config, data, setData, setConfig }) => {
  useEffect(() => {
    const dataW = JSON.parse(window.localStorage.getItem("auth"));
    const fetchData = async () => {
      await axios
        .get("http://localhost:5000/api/nhaban/doanhthu/" + dataW.MaNhaBan)
        .then((res) => {
          setData(
            [
            {
              name: "Đơn hàng",
              date: "Jan.",
              money: res.data[0].ThangMot,
            },
            {
              name: "Đơn hàng",
              date: "Feb.",
              money: res.data[0].ThangHai,
            },
            {
              name: "Đơn hàng",
              date: "Mar.",
              money: res.data[0].ThangBa,
            },
            {
              name: "Đơn hàng",
              date: "Apr.",
              money: res.data[0].ThangTu,
            },
            {
              name: "Đơn hàng",
              date: "May",
              money: res.data[0].ThangNam,
            },
            {
              name: "Đơn hàng",
              date: "Jun.",
              money: res.data[0].ThangSau,
            },
            {
              name: "Đơn hàng",
              date: "Jul.",
              money: res.data[0].ThangBay,
            },
            {
              name: "Đơn hàng",
              date: "Aug.",
              money: res.data[0].ThangTam,
            },
            {
              name: "Đơn hàng",
              date: "Sep.",
              money: res.data[0].ThangChin,
            },
            {
              name: "Đơn hàng",
              date: "Oct.",
              money: res.data[0].ThangMuoi,
            },
            {
              name: "Đơn hàng",
              date: "Nov.",
              money: res.data[0].ThangMuoiMot,
            },
            {
              name: "Đơn hàng",
              date: "Dec.",
              money: res.data[0].ThangMuoiHai,
            },
          ]);
          setConfig({
            data: data,
            isGroup: true,
            xField: "date",
            yField: "money",
            seriesField: "name",
            label: {
              position: "middle",
              layout: [
                { type: "interval-adjust-position" },
                { type: "interval-hide-overlap" },
                { type: "adjust-color" },
              ],
            },
            xAxis: {
              label: {
                autoHide: true,
                autoRotate: false,
              },
            },
          });
        });
    };
    
    fetchData();
  }, []);

  return (
    <div>
      <Column {...config} />
    </div>
  );
};
export default Chart;
