import { Layout, Typography, Form, Input, Button, Checkbox } from "antd";
import { useHistory } from "react-router-dom";
import { UserOutlined, LockOutlined } from "@ant-design/icons";
import "./style.css";
import DangKy from "./dangKy";
import axios from "axios";

const { Title } = Typography;
const { Content } = Layout;

const DangNhap = () => {
  const history = useHistory();
  const[loginInfo, setLoginInfo] = useState({
    email: "",
    password: "",
  });
  const [listNhaBan, setListNhaBan] = useState([]);
  const onFinish = (values) => {
    console.log("Received values of form: ", values);
    history.push("/nhaban/");

    axios.get("http://localhost:5000/api/nhaban").then((response) => {
      console.log("Received response: ", response.data);
    }).catch((error) => {
      console.log("Received error: ", error);
    })
  };
  return (
    <Layout>
      <div className="titleDN">
        <Title>Đăng Nhập Bán Hàng</Title>
      </div>
      <div className="boxDN">
        <Form
          name="normal_login"
          className="login-form"
          initialValues={{
            remember: true,
          }}
          onFinish={onFinish}
        >
          <Form.Item
            name="Tên đăng nhập"
            rules={[
              {
                required: true,
                message: "Please input your Username!",
              },
            ]}
          >
            <Input
              prefix={<UserOutlined className="site-form-item-icon" />}
              placeholder="Tên đăng nhập"
            />
          </Form.Item>
          <Form.Item
            name="Mật khẩu"
            rules={[
              {
                required: true,
                message: "Please input your Password!",
              },
            ]}
          >
            <Input.Password
              prefix={<LockOutlined className="site-form-item-icon" />}
              type="password"
              placeholder="Mật khẩu"
            />
          </Form.Item>

          <Form.Item className="btnDN">
            <Button
              type="primary"
              htmlType="submit"
              className="login-form-button"
              style={{ margin: 2 }}
            >
              Đăng nhập
            </Button>
            Hoặc <a href="/nhaban/dangKy">Đăng ký ngay!</a>
          </Form.Item>
        </Form>
      </div>
    </Layout>
  );
};

export default DangNhap;
