import { Layout, Typography, Row, Col, Statistic, Button, Card } from "antd";
import React, { useState, useEffect } from "react";
import { Column } from "@ant-design/charts";
const { Title } = Typography;
const { Content } = Layout;

const TrangChu = () => {
  const data = [
    {
      type: "Furniture appliances",
      sales: 38,
    },
    {
      type: "Cereals, Oils and Non-staple food",
      sales: 52,
    },
    {
      type: "Fresh fruits",
      sales: 0,
    },
    {
      type: "Beauty care",
      sales: 145,
    },
    {
      type: "Baby products",
      sales: 48,
    },
    {
      type: "Imported food",
      sales: 38,
    },
    {
      type: "Food and drink",
      sales: 38,
    },
    {
      type: "Home cleaning",
      sales: 38,
    },
  ];

  const config = {
    data,
    xField: "type",
    yField: "sales",
    label: {
      position: "middle",
      style: {
        fill: "#FFFFFF",
        opacity: 0.6,
      },
    },
    meta: {
      type: { alias: "Category" },
      sales: { alias: "Sales" },
    },
  };
  return (
    <Layout>
      <Content>
        <div>
          <Title level={2}>Trang Chủ</Title>
        </div>

        <Row>
          <Col span="9">
            <Row>
              <Card title="Tổng đơn hàng" style={{ width: 700 }} extra={0}>
                <Row gutter={16}>
                  <Col span={12}>
                    <Statistic
                      title="Chờ nhận hàng"
                      value={112893}
                      valueStyle={{ color: "#017fff" }}
                    />
                  </Col>
                  <Col span={12}>
                    <Statistic
                      title="Chờ xác nhận"
                      value={112893}
                      valueStyle={{ color: "#017fff" }}
                    />
                  </Col>
                </Row>
              </Card>
            </Row>
            <br />
            <Row>
              <Card title="Sản phẩm đang bán" style={{ width: 700 }} extra={0}>
                <Row gutter={16}>
                  <Col span={12}>
                    <Statistic
                      title="Sản phẩm đang bán"
                      value={112893}
                      valueStyle={{ color: "#017fff" }}
                    />
                  </Col>
                  <Col span={12}>
                    <Statistic
                      title="Sản phẩm hết tồn"
                      value={112893}
                      valueStyle={{ color: "#017fff" }}
                      tooltip="Sản phẩm không còn trong kho"
                    />
                  </Col>
                </Row>
                <Row gutter={16}>
                  <Col span={12}>
                    <Statistic
                      title="Sản phẩm bị từ chối"
                      value={112893}
                      valueStyle={{ color: "#017fff" }}
                      tooltip="Sản phẩm không bị hủy"
                    />
                  </Col>
                  <Col span={12}>
                    <Statistic
                      title="Sản phẩm bị tắt"
                      value={112893}
                      valueStyle={{ color: "#017fff" }}
                      tooltip="Sản phẩm không được trưng bày"
                    />
                  </Col>
                </Row>
              </Card>
            </Row>
          </Col>
          <Col span="14">
            <Card
              title="Hiệu quả hoạt động"
              style={{ width: 800 }}
              extra={<a href="#">Xem chi tiết</a>}
            >
              <Column
                {...config}
                onReady={(plot) => {
                  plot.on("plot:click", (evt) => {
                    const { x, y } = evt;
                    const { xField } = plot.options;
                    const tooltipData = plot.chart.getTooltipItems({ x, y });
                    console.log(tooltipData);
                  });
                }}
              />
            </Card>
          </Col>
        </Row>
      </Content>
    </Layout>
  );
};
export default TrangChu;
