import React, { useState, useEffect } from "react";
import DNhap from "./dangNhap";
import { useHistory } from "react-router-dom";
import axios from "axios";
import "./style.css";
import {
  Layout,
  Form,
  Input,
  Typography,
  Select,
  Row,
  Col,
  Checkbox,
  Button,
  AutoComplete,
} from "antd";
const { Option } = Select;
const { Title, Text } = Typography;

const formItemLayout = {
  labelCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 8,
    },
  },
  wrapperCol: {
    xs: {
      span: 24,
