          <Form.Item
            name="TenCH"
            label="T√™n c·ª≠a h√†ng"
            rules={[
              {
                required: true,
                message: "Please input your store name!",
                whitespace: true,
              },
            ]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            name="email"
            label="E-mail"
            rules={[
              {
                type: "email",
                message: "The input is not valid E-mail!",
              },
              {
                required: true,
                message: "Please input your E-mail!",
              },
            ]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            name="phone"
            label="S·ªë ƒëi·ªán tho·∫°i"
            rules={[
              {
                required: true,
                message: "Please input your phone number!",
              },
            ]}
          >
            <Row gutter={8}>
              <Col span={12}>
                <Input
                  style={{
                    width: "100%",
                  }}
                />
              </Col>
            </Row>
          </Form.Item>

          <Form.Item
            name="residence"
            label="ƒê·ªãa ch·ªâ"
            rules={[
              {
                type: "array",
                required: true,
                message: "Please select your habitual residence!",
              },
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="password"
            label="M·∫≠t kh·∫©u"
            rules={[
              {
                required: true,
                message: "Please input your password!",
              },
            ]}
            hasFeedback
          >
            <Input.Password />
          </Form.Item>

          <Form.Item
            name="confirm"
            label="X√°c th·ª±c m·∫≠t kh·∫©u"
            dependencies={["password"]}
            hasFeedback
            rules={[
              {
                required: true,
                message: "Please confirm your password!",
              },
              ({ getFieldValue }) => ({
                validator(_, value) {
                  if (!value || getFieldValue("password") === value) {
                    return Promise.resolve();
                  }

                  return Promise.reject(
                    new Error(
                      "The two passwords that you entered do not match!"
                    )
                  );
                },
              }),
            ]}
          >
            <Input.Password />
          </Form.Item>

          <Form.Item
            name="agreement"
            valuePropName="checked"
            rules={[
              {
                validator: (_, value) =>
                  value
                    ? Promise.resolve()
                    : Promise.reject(new Error("Should accept agreement")),
              },
            ]}
            {...tailFormItemLayout}
          >
            <Checkbox>
              T√¥i ƒë·ªìng √Ω v·ªõi <a href="">ch√≠nh s√°ch c·ªßa Tiki</a>
            </Checkbox>
          </Form.Item>
          <Form.Item {...tailFormItemLayout}>
            <Button type="primary" htmlType="submit" className="btnDK">
              ƒêƒÉng k√Ω
            </Button>
            <div style={{ margin: 2 }}>
              <a href="/nhaban/dangNhap">
                B·∫°n ƒë√£ c√≥ t√†i kho·∫£n. ƒêƒÉng nh·∫≠p ngay!
              </a>
            </div>
          </Form.Item>
        </Form>
      </div>
    </Layout>
  );
};

export default DangKy;
