import TableItem from "./cartComponents/TableItem";
import InfoPanel from "./cartComponents/InfoPanel";
import { Row, Col } from "antd";
import "./Cart.css";
import { useState, useEffect } from "react";
import axios from "axios";

const Cart = () => {
  const [cart, setCart] = useState([]);
  const [bill, setBill] = useState([]);
  const [finalBill, setFinalBill] = useState([]);

  const fetchBill = () => {
    let temp = [];
    let t;
    if (window.localStorage.getItem("cart").length !== 0) {
      t = JSON.parse(window.localStorage.getItem("cart"));
    }

    for (let i = 0; i < t.length; i++) {
      axios
        .get("http://localhost:5000/api/ctsp/" + t[i].MaSP)
        .then((res) => {
          console.log(temp);
          temp.push(res.data[0]);
        })
        .catch((err) => {
          console.log(err);
        });
    }

    return t;
  };

  useEffect(() => {
    const t = fetchBill();

    const temp = bill.map((item, pos) => {
      return {
        key: pos,
        nameItem: item.TenSP,
        price: item.ThanhTienSP,
        amount: cart[pos].SoLuong,
        total: item.ThanhTienSP * cart[pos].SoLuong,
      };
    });
    setFinalBill(temp);
  }, []);

  return (
    <div className="wrap-cart">
      <h3>Giỏ hàng</h3>
      <Row>
        <Col flex={10}>
          <TableItem bill={finalBill} />
        </Col>
        <Col flex={2}></Col>
        <Col flex="300px">
          <InfoPanel bill={finalBill} />
        </Col>
      </Row>
    </div>
  );
};

export default Cart;
