import { Table } from "antd";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { Tabs, Layout } from "antd";
import { Typography } from "antd";
import "./style.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
const { Title } = Typography;
const { TabPane } = Tabs;
const { Content } = Layout;
function callback(key) {
  console.log(key);
}
const columns = [
  {
    title: "Mã Chi tiết HĐ",
    dataIndex: "MaCTHD",
    key: "MaCTHD",
    render: (text) => <Link to="/nhaban/tthd">{text}</Link>,
  },
  {
    title: "Tên Sản Phẩm",
    dataIndex: "TenSP",
    key: "TenSP",
  },
  {
    title: "Số lượng",
    dataIndex: "SoLug",
    key: "SoLug",
  },
  {
    title: "Giá Tiền",
    dataIndex: "GiaTien",
    key: "GiaTien",
  },
];
const data = [
  {
    MaCTHD: "1",
    TenSP: "John Brown",
    SoLug: 32,
    GiaTien: "100000",
  },
  {
    MaCTHD: "2",
    TenSP: "Jim Green",
    SoLug: 42,
    GiaTien: "100000",
  },
  {
    MaCTHD: "3",
    TenSP: "Joe Black",
    SoLug: 32,
    GiaTien: "100000",
  },
];

function DsHoaDon() {
  return (
    <div>
      <Layout>
        <Content>
          <Title level={2} classname="titlename">
            Danh sách Chi Tiết Hóa Đơn
          </Title>

          <Table columns={columns} dataSource={data} />
        </Content>
      </Layout>
    </div>
  );
}

export default DsHoaDon;
