import { Form, Input, InputNumber, Select, Button } from 'antd';
import { DatePicker } from 'antd';
import { Layout } from 'antd';
import { Typography } from 'antd';

const { Title } = Typography;
const { Option } = Select;
const layout = {
    labelCol: { span: 8 },
    wrapperCol: { span: 10 },
  };

function onChange(date, dateString) {
console.log(date, dateString);
}

const onFinish = (values) => {
    const listAddress = values.address.split(", ");

    const info = {
      MaKH: amount,
      Email: values.email,
      MatKhau: values.password,
      Ten: values.name,
      NgaySinh: moment(values.dateOfBirth).format("MM-DD-YYYY"),
      Sdt: values.phone,
      GioiTinh: values.gender === "male" ? "Nam" : "Nữ",
      SoNha: listAddress[0],
      Duong: listAddress[1],
      Phuong: listAddress[2],
      Quan: listAddress[3],
      ThanhPho: listAddress[4],
      TikiXu: 0,
    };

    axios
      .post("http://localhost:5000/api/addkhachhang", JSON.stringify(info), {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((res) => {
        console.log(res);
        history.push("/login");
      })
      .catch((err) => {
        console.log(err);
      });
  };


  const TaoNVQLK = () => {
    return (     
        <Layout>
            <div>
                <Title level="2" align="center">Thêm Nhân Viên Quản Lý Kho</Title>
            </div>
            <div>
            <Form {...layout} onFinish={onFinish}>
                <Form.Item label="Nhập mã nhân viên" >
                    <Input />
                </Form.Item>     
                <Form.Item label="Nhập tên nhân viên" >
                    <Input />
                </Form.Item>    
                <Form.Item name={['user', 'email']} label="Email" >
                    <Input />
                </Form.Item>
                
                <Form.Item label="Số điện thoại" >
                    <Input />
                </Form.Item>

                <Form.Item wrapperCol={{span: 3}} label="Ngày sinh" >
                    <DatePicker onChange={onChange} />
                </Form.Item>

                <Form.Item
                    wrapperCol={{span: 3}}
                    name="gioiTinh"
                    label="Giới tính"
                    rules={[{ required: true, message: 'Vui lòng chọn giới tính!' }]}
                >
                    <Select placeholder="Chọn giới tính">
                        <Option value="Nam">Nam</Option>
                        <Option value="Nữ">Nữ</Option>
                    </Select>
                </Form.Item>

                <Form.Item label="Địa chỉ">
                    <Input.Group compact>
                    <Form.Item
                        name={['thanhPho']}
                        noStyle
                    >
                        <Input style={{ width: '35%' }} placeholder="Nhập thành phố" />
                    </Form.Item>
                    <Form.Item
                        name={['quan']}
                        noStyle
                    >
                        <Input style={{ width: '35%' }} placeholder="Nhập quận" />
                    </Form.Item>
                    <Form.Item
                        name={['phuong']}
                        noStyle
                    >
                        <Input style={{ width: '30%' }} placeholder="Nhập phường" />
                    </Form.Item>
                    <Form.Item
                        name={['duong']}
                        noStyle
                    >
                        <Input placeholder="Nhập tên đường" />
                    </Form.Item>
                    </Input.Group>
                </Form.Item>

                <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
                <Button type="primary" htmlType="submit" size="middle">
                    Xác nhận
                </Button>
                </Form.Item>
            </Form>
            </div>
        </Layout>  
    );
  };
export default TaoNVQLK;
