/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from "react";
import { Table, Radio, Divider } from "antd";

const columns = [
  {
    title: "Tên sản phẩm",
    dataIndex: "nameItem",
    render: (text) => <a>{text}</a>,
  },
  {
    title: "Đơn giá",
    dataIndex: "price",
  },
  {
    title: "Số lượng",
    dataIndex: "amount",
  },
  {
    title: "Thành tiền",
    dataIndex: "total",
  },
  {
    title: "Delete",
    render: (
      <>
        {tags.map((tag) => {
          let color = tag.length > 5 ? "geekblue" : "green";
          if (tag === "loser") {
            color = "volcano";
          }
          return (
            <Tag color={color} key={tag}>
              {tag.toUpperCase()}
            </Tag>
          );
        })}
      </>
    ),
  },
];
const data = [
  {
    key: "1",
    nameItem: "John Brown",
    price: 32,
    amount: "New York No. 1 Lake Park",
    total: 200000
  },
  {
    key: "2",
    nameItem: "Jim Green",
    price: 42,
    amount: "London No. 1 Lake Park",
    total: 200000
  },
  {
    key: "3",
    nameItem: "Joe Black",
    price: 32,
    amount: "Sidney No. 1 Lake Park",
    total: 200000
  },
]; // rowSelection object indicates the need for row selection

const rowSelection = {
  onChange: (selectedRowKeys, selectedRows) => {
    console.log(
      `selectedRowKeys: ${selectedRowKeys}`,
      "selectedRows: ",
      selectedRows
    );
  },
  getCheckboxProps: (record) => ({
    disabled: record.name === "Disabled User",
    // Column configuration not to be checked
    name: record.name,
  }),
};

const TableItem = () => {
  return (
    <div>
      <Table
        rowSelection={{
          type: "checkbox",
          ...rowSelection,
        }}
        columns={columns}
        dataSource={data}
      />
    </div>
  );
};

export default TableItem;
