import "./style.css";
import {
  Layout,
  Card,
  Input,
  Col,
  Row,
  Select,
  Table,
  Button,
  Menu,
  Dropdown,
  message,
} from "antd";
import { Typography } from "antd";

import { Icon } from 'antd';
const { Content } = Layout;

const columns = [
  {
    title: "Mã Chi tiết HĐ",
    dataIndex: "MaCTHD",
    key: "MaCTHD",
  },
  {
    title: "Tên Sản Phẩm",
    dataIndex: "TenSP",
    key: "TenSP",
    render: (text) => <a>{text}</a>,
  },
  {
    title: "Số lượng",
    dataIndex: "SoLug",
    key: "SoLug",
  },
  {
    title: "Giá Tiền",
    dataIndex: "GiaTien",
    key: "GiaTien",
  },
];

const data = [
  {
    MaCTHD: "1",
    TenSP: "John Brown",
    SoLug: 32,
    GiaTien: "100000",
  },
  {
    MaCTHD: "2",
    TenSP: "Jim Green",
    SoLug: 42,
    GiaTien: "100000",
  },
  {
    MaCTHD: "3",
    TenSP: "Joe Black",
    SoLug: 32,
    GiaTien: "100000",
  },
];
function handleButtonClick(e) {
  message.info("Click on left button.");
  console.log("click left button", e);
}

function handleMenuClick(e) {
  message.info("Click on menu item.");
  console.log("click", e);
}
const menu = (
  <Menu onClick={handleMenuClick}>
    <Menu.Item key="1">1st menu item</Menu.Item>
    <Menu.Item key="2">2nd menu item</Menu.Item>
    <Menu.Item key="3">3rd item</Menu.Item>
  </Menu>
);
const ThongTinHD = () => {
  return (
    <Layout>
      <Content>
        <div className="site-input-group-wrapper">
          <div className="site-card-border-less-wrapper">
            <Row>
              <Col span={10}>
                <Card
                  title="Thông Tin Hóa Đơn"
                  bordered={false}
                  style={{ width: 800 }}
                >
                  <Input.Group size="medium">
                    <Row gutter={8}>
                      <Col span={5}>
                        <Input defaultValue="Mã Hóa Đơn" />
                        <Input defaultValue="Ngày lập Hóa Đơn" />
                        <Input defaultValue="Tổng tiền" />
                      </Col>
                      <Col span={8}>
                        <Input defaultValue="26888888" />
                        <Input defaultValue="6:30 12/08/2021" />
                        <Input defaultValue="0571" />
                      </Col>
                    </Row>
                  </Input.Group>
                </Card>
              </Col>
              <Col span={14}>
                <Card
                  title="Thông Tin Khách Hàng"
                  bordered={false}
                  style={{ width: 1200 }}
                >
                  <Input.Group size="medium">
                    <Row gutter={8}>
                      <Col span={5}>
                        <Input defaultValue="Họ Tên" />
                        <Input defaultValue="Địa chỉ" />
                        <Input defaultValue="Số điện thoại" />
                      </Col>
                      <Col span={8}>
                        <Input defaultValue="Nguyễn Trọng Khang" />
                        <Input defaultValue="149 Nguyễn Trọng Tuyển, P.8, Quận Phú Nhuận, TP.HCM" />
                        <Input defaultValue="0571665464" />
                      </Col>
                    </Row>
                  </Input.Group>
                </Card>
              </Col>
            </Row>
          </div>

          <div className="site-card-border-less-wrapper">
            <Card
              title="Chi Tiết Hóa Đơn"
              bordered={false}
              style={{ width: 1100 }}
            >
              <Table columns={columns} dataSource={data} />
            </Card>
          </div>

          <div>
            <Row>
              <Col span={12}>
                <Row>
                  <Col>
                    <Title level="5">Tình trạng: </Title>
                  </Col>
                  <Col>
                    <div>
                      <Dropdown.Button
                        onClick={handleButtonClick}
                        overlay={menu}
                      >
                        Dropdown
                      </Dropdown.Button>
                      <Dropdown.Button
                        onClick={handleButtonClick}
                        overlay={menu}
                        disabled
                        style={{ marginLeft: 8 }}
                      >
                        Dropdown
                      </Dropdown.Button>
                      <Dropdown overlay={menu}>
                        <Button style={{ marginLeft: 8 }}>
                          Button <Icon type="down" />
                        </Button>
                      </Dropdown>
                    </div>
                  </Col>
                </Row>
              </Col>
              <Col span={12}>
                <Button type="primary">Primary Button</Button>
              </Col>
            </Row>
          </div>
        </div>
      </Content>
    </Layout>
  );
};
export default ThongTinHD;
