import "./style.css";
import React, { useState } from 'react';
import {
  Layout,
  Card,
  Input,
  Col,
  Row,
  Select,
  Table,
  Button,
  Typography,
} from "antd";

import { HighlightOutlined, SmileOutlined, SmileFilled } from '@ant-design/icons';
const { Content } = Layout;
const { Title, Paragraph } = Typography;
const { Option } = Select;
const columns = [
  {
    title: "Mã Chi tiết HĐ",
    dataIndex: "MaCTHD",
    key: "MaCTHD",
  },
  {
    title: "Tên Sản Phẩm",
    dataIndex: "TenSP",
    key: "TenSP",
    render: (text) => <a>{text}</a>,
  },
  {
    title: "Số lượng",
    dataIndex: "SoLug",
    key: "SoLug",
  },
  {
    title: "Giá Tiền",
    dataIndex: "GiaTien",
    key: "GiaTien",
  },
];

const data = [
  {
    MaCTHD: "1",
    TenSP: "John Brown",
    SoLug: 32,
    GiaTien: "100000",
  },
  {
    MaCTHD: "2",
    TenSP: "Jim Green",
    SoLug: 42,
    GiaTien: "100000",
  },
  {
    MaCTHD: "3",
    TenSP: "Joe Black",
    SoLug: 32,
    GiaTien: "100000",
  },
];
function handleChange(value) {
  console.log(`selected ${value}`);
}
const Demo: React.FC = () => {
    const [editableStr, setEditableStr] = useState('This is an editable text.');
    const [customIconStr, setCustomIconStr] = useState('Custom Edit icon and replace tooltip text.');
    const [hideTooltipStr, setHideTooltipStr] = useState('Hide Edit tooltip.');
    const [lengthLimitedStr, setLengthLimitedStr] = useState(
      'This is an editable text with limited length.',
    );
}
const ThongTinHD = () => {
  return (
    <Layout>
      <Content>
        <div className="site-input-group-wrapper">
          <div className="site-card-border-less-wrapper">
            <Row>
              <Col span={10}>
                <Card
                  title="Thông Tin Hóa Đơn"
                  bordered={false}
                  style={{ width: 800 }}
                >
                  <Input.Group size="medium">
                    <Row gutter={8}>
                      <Col span={5}>
                        <Input defaultValue="Mã Hóa Đơn" />
                        <Input defaultValue="Ngày lập Hóa Đơn" />
                        <Input defaultValue="Tổng tiền" />
                      </Col>
                      <Col span={8}>
                        <Input defaultValue="26888888" />
                        <Input defaultValue="6:30 12/08/2021" />
                        <Input defaultValue="0571" />
                      </Col>
                    </Row>
                  </Input.Group>
                </Card>
              </Col>
              <Col span={14}>
                <Card
                  title="Thông Tin Khách Hàng"
                  bordered={false}
                  style={{ width: 1200 }}
                >
                  <Input.Group size="medium">
                    <Row gutter={8}>
                      <Col span={5}>
                        <Input defaultValue="Họ Tên" />
                        <Input defaultValue="Địa chỉ" />
                        <Input defaultValue="Số điện thoại" />
                      </Col>
                      <Col span={8}>
                        <Input defaultValue="Nguyễn Trọng Khang" />
                        <Input defaultValue="149 Nguyễn Trọng Tuyển, P.8, Quận Phú Nhuận, TP.HCM" />
                        <Input defaultValue="0571665464" />
                      </Col>
                    </Row>
                  </Input.Group>
                </Card>
              </Col>
            </Row>
          </div>

          <div className="site-card-border-less-wrapper">
            <Card
              title="Chi Tiết Hóa Đơn"
              bordered={false}
              style={{ width: 1100 }}
            >
              <Table columns={columns} dataSource={data} />
            </Card>
          </div>

          <div>
            <Row>
              <Col>
                <Row className="tinhTragTTHD">
                  <Col>
                    <Title level={4}>Tình trạng: </Title>
                  </Col>
                  <Col>
                    <div>
                      <Select
                        defaultValue="Chờ xác nhận"
                        style={{ width: 150, margin:2 }}
                        onChange={handleChange}
                      >
                        <Option value="Đang xử lý">Đang xử lý</Option>
                        <Option value="Đang vận chuyển">Đang vận chuyển</Option>
                        <Option value="Đang giao">Đang giao</Option>
                        <Option value="Đang giao">Đã hủy</Option>
                      </Select>
                    </div>
                  </Col>
                </Row>
              </Col>
              <Col>
                <Button type="primary" className="btnSaveTTHD">
                  Lưu
                </Button>
              </Col>
            </Row>
          </div>
        </div>
      </Content>
    </Layout>
  );
};
export default ThongTinHD;
