import { Image, Row, Col, Button, InputNumber, Card, Tag } from "antd";
import Icon from "@ant-design/icons";
import { useState, useEffect } from "react";
import axios from "axios";

const StarSvg = () => (
  <svg height="15px" viewBox="0 -10 511.98685 511" width="15px">
    <path
      d="m510.652344 185.902344c-3.351563-10.367188-12.546875-17.730469-23.425782-18.710938l-147.773437-13.417968-58.433594-136.769532c-4.308593-10.023437-14.121093-16.511718-25.023437-16.511718s-20.714844 6.488281-25.023438 16.535156l-58.433594 136.746094-147.796874 13.417968c-10.859376 1.003906-20.03125 8.34375-23.402344 18.710938-3.371094 10.367187-.257813 21.738281 7.957031 28.90625l111.699219 97.960937-32.9375 145.089844c-2.410156 10.667969 1.730468 21.695313 10.582031 28.09375 4.757813 3.4375 10.324219 5.1875 15.9375 5.1875 4.839844 0 9.640625-1.304687 13.949219-3.882813l127.46875-76.183593 127.421875 76.183593c9.324219 5.609376 21.078125 5.097657 29.910156-1.304687 8.855469-6.417969 12.992187-17.449219 10.582031-28.09375l-32.9375-145.089844 111.699219-97.941406c8.214844-7.1875 11.351563-18.539063 7.980469-28.925781zm0 0"
      fill="#ffc107"
    />
  </svg>
);
const StoreSvg = () => (
  <svg viewBox="0 0 291.775 291.775">
    <g>
      <path />
      <path />
      <path />
      <g>
        <path />
      </g>
      <path />
      <g>
        <rect width="9.118" height="27.353" />
        <polygon />
        <rect width="18.235" height="45.588" />
        <polygon />
      </g>
      <g>
        <rect width="54.706" height="18.235" />
        <rect width="54.706" height="18.235" />
        <rect width="54.706" height="18.235" />
      </g>
      <g>
        <path />
      </g>
      <g>
        <path />
        <path />
      </g>
      <polygon />
      <g>
        <polygon />
        <path />
        <polygon />
        <polygon />
        <path />
        <rect width="18.235" height="54.669" />
        <polygon />
      </g>
      <g>
        <polygon />
        <polygon />
        <rect width="18.235" height="27.353" />
        <polygon />
        <polygon />
        <polygon />
      </g>
      <polygon />
    </g>
  </svg>
);

const StarIcon = (props) => <Icon component={StarSvg} {...props} />;
const StoreIcon = (props) => <Icon component={StoreSvg} {...props} />;


const description = (
  <div>
    4.2/5 <StarIcon />
  </div>
);

function ProductDetail() {
  const [product, setProduct] = useState({});
  const [store, setStore] = useState({});

  useEffect(() => {
    const path = window.location.pathname;
    const id = path.split("/")[2];
    axios
      .get("http://localhost:5000/api/ctsp/" + id)
      .then((res) => {
        if (res.data.lenght !== 0) {
          setProduct(res.data[0]);
        }
      })
      .catch((err) => {
        console.log(err);
      });

    axios.get("http://localhost:5000/api/nbsp/" + id).then((res) => {
      if (res.data.lenght !== 0) {
        setStore(res.data[0]);
      }
    });
  }, []);

  const { Meta } = Card;
  function onChange(value) {
    console.log("changed", value);
  }

  const storeDescription = (
    <div>
      <StoreIcon /> {store.TenNhaBan}
    </div>
  ); 

  return (
    <div>
      <Row>
        <Col span={12} offset={6}>
          <Row>
            <Col flex="300px">
              <Image
                width={200}
                src="https://salt.tikicdn.com/cache/w200/ts/product/25/fd/0b/b3c2299af08efca753341bafb7d7b627.jpg"
              />
            </Col>
            <Col flex="auto">
              <Row>
                <Col flex="auto">
                  <div className="product-content">
                    <p style={{ fontSize: 25 }}>{product.TenSP}</p>
                    <b style={{ fontSize: 30 }}>
                      {product.ThanhTienSP}.000 Đồng
                    </b>
                    <div>
                      <Tag color="#ff424e">
                        -
                        {Math.ceil(
                          (product.GiaGiamSP / product.GiaBanSP) * 100
                        )}
                        %
                      </Tag>
                    </div>
                    <div>Số lượng</div>
                    <InputNumber
                      min={1}
                      max={10}
                      defaultValue={1}
                      onChange={onChange}
                    />
                    <Button type="primary" danger>
                      Chọn mua
                    </Button>
                  </div>
                </Col>
                <Col flex="auto">
                  <div></div>
                  <div className="product-owner">
                    <Card
                      title={storeDescription}
                      style={{ width: 300, marginTop: 16 }}
                    >
                      <Meta description={description} />
                    </Card>
                  </div>
                </Col>
              </Row>
            </Col>
          </Row>
        </Col>
      </Row>
    </div>
  );
}

export default ProductDetail;
