import "./style.css";
import { Layout, Card, Typography } from "antd";
import { Form, Input, Button } from "antd";
import React, { useState, useEffect } from "react";
import axios from "axios";
const { Content } = Layout;
const { Title} = Typography;
const formItemLayout = {
  labelCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 8,
    },
  },
  wrapperCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 16,
    },
  },
};

const ThongTinHD = () => {
  const [form] = Form.useForm();
  let dataW = JSON.parse(window.localStorage.getItem("auth"));
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/nhaban")
      .then((response) => {
        if (response.data.length > 0) {
          setAmount(response.data[0].slSP);
          console.log(response.data);
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);
  const onFinish = (values) => {
   
    console.log("Received values of form: ", values);

  };
  return (
    <Layout>
      <Content>
        <div className="contentTTNB">
          <div className="site-card-border-less-wrapper">
            <div>
              <Title level={2}>Thông Tin Nhà Bán</Title>
            </div>
            <Card bordered={false}>
              <div className="formTTNB">
                <Form
                  {...formItemLayout}
                  form={form}
                  onFinish={onFinish}
                  scrollToFirstError
                  wrapperCol={{ span: 10 }}
                >
                  <Form.Item
                    name="manb"
                    label="Mã Nhà Bán"
                    rules={[{ type: "manb" }, { required: true }]}
                  >
                    <Input defaultValue="26888888" />
                  </Form.Item>

                  <Form.Item
                    name="tennb"
                    label="Tên Nhà Bán"
                    rules={[{ type: "tennb" }, { required: true }]}
                  >
                    <Input defaultValue="Nguyễn Trọng Khang" />
                  </Form.Item>

                  <Form.Item
                    name="email"
                    label="E-mail"
                    rules={[
                      {
                        type: "email",
                      },
                      {
                        required: true,
                      },
                    ]}
                  >
                    <Input type="email" defaultValue="abc@gmail.com" />
                  </Form.Item>

                  <Form.Item
                    name="sdt"
                    label="Số Điện Thoại"
                    rules={[
                      {
                        required: true,
                      },
                    ]}
                  >
                    <Input defaultValue="0485461213" />
                  </Form.Item>

                  <Form.Item
                    name="password"
                    label="Mật khẩu "
                    rules={[
                      {
                        required: true,
                      },
                    ]}
                  >
                    <Input.Password
                      placeholder="input password"
                      defaultValue="fsdhfkshfsf"
                    />
                  </Form.Item>

                  <Form.Item
                    name="sonha"
                    label="Số nhà"
                    rules={[{ required: true }]}
                  >
                    <Input defaultValue="149" />
                  </Form.Item>

                  <Form.Item
                    name="duong"
                    label="Đường"
                    rules={[{ required: true }]}
                  >
                    <Input defaultValue="Nguyễn Trọng Tuyển" />
                  </Form.Item>

                  <Form.Item
                    name="phuong"
                    label="Phường"
                    rules={[{ required: true }]}
                  >
                    <Input defaultValue="8" />
                  </Form.Item>

                  <Form.Item
                    name="quan"
                    label="Quận"
                    rules={[{ required: true }]}
                  >
                    <Input defaultValue="Phú Nhuận" />
                  </Form.Item>

                  <Form.Item
                    name="thanhpho"
                    label="Thành Phố"
                    rules={[{ required: true }]}
                  >
                    <Input defaultValue="Hồ Chí Minh" />
                  </Form.Item>

                  <Form.Item >
                    <Button type="primary" htmlType="submit" className="btnSaveTTNB">
                      Lưu
                    </Button>
                  </Form.Item>
                </Form>
              </div>
            </Card>
          </div>
         
        </div>
      </Content>
    </Layout>
  );
};
export default ThongTinHD;
