import React, { useState, useEffect } from "react";
import DNhap from "./dangNhap";
import { useHistory } from "react-router-dom";
import axios from "axios";
import "./style.css";
import {
  Layout,
  Form,
  Input,
  Typography,
  Select,
  Row,
  Col,
  Checkbox,
  Button,
  AutoComplete,
} from "antd";
const { Option } = Select;
const { Title, Text } = Typography;

const formItemLayout = {
  labelCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 8,
    },
  },
  wrapperCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 16,
    },
  },
};
const tailFormItemLayout = {
  wrapperCol: {
    xs: {
      span: 24,
      offset: 0,
    },
    sm: {
      span: 16,
      offset: 8,
    },
  },
};

const DangKy = () => {
  const [form] = Form.useForm();
  const history = useHistory();
  const [amount, setAmount] = useState(0);

  useEffect(() => {
    axios
    .post("http://localhost:5000/api/nhaban").then((response) => {
      if(response.data.length > 0) {
        setAmount(response.data[0].slNB);
        console.log(response.data);
      }
    }).catch((error) => {
      console.error(error);
    })
  }, [])
  const onFinish = (values) => {
    const info = {
      email: values.email,
      password: values.password,
    };


    console.log("Received values of form: ", values);
    history.push("/nhaban/dangnhap");
  };

  const prefixSelector = (
    <Form.Item name="prefix" noStyle>
      <Select
        style={{
          width: 70,
        }}
      >
        <Option value="86">+86</Option>
        <Option value="87">+87</Option>
      </Select>
    </Form.Item>
  );
  const [autoCompleteResult, setAutoCompleteResult] = useState([]);



  const websiteOptions = autoCompleteResult.map((website) => ({
    label: website,
    value: website,
  }));
  return (
    <Layout>
      <div className="titleDK">
        <Title>Đăng Ký Bán Hàng cùng Tiki</Title>
      </div>
      <div className="formDK">
        <Form
          {...formItemLayout}
          form={form}
          name="register"
          onFinish={onFinish}
          initialValues={{
            residence: ["zhejiang", "hangzhou", "xihu"],
            prefix: "86",
          }}
          scrollToFirstError
        >
          <Form.Item
            name="TenCH"
            label="Tên cửa hàng"
            rules={[
              {
                required: true,
                message: "Please input your store name!",
                whitespace: true,
              },
            ]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            name="email"
            label="E-mail"
            rules={[
              {
                type: "email",
                message: "The input is not valid E-mail!",
              },
              {
                required: true,
                message: "Please input your E-mail!",
              },
            ]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            name="phone"
            label="Số điện thoại"
            rules={[
              {
                required: true,
                message: "Please input your phone number!",
              },
            ]}
          >
            <Row gutter={8}>
              <Col span={12}>
                <Input
                  style={{
                    width: "100%",
                  }}
                />
              </Col>
            </Row>
          </Form.Item>

          <Form.Item
            name="residence"
            label="Địa chỉ"
            rules={[
              {
                type: "array",
                required: true,
                message: "Please select your habitual residence!",
              },
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="password"
            label="Mật khẩu"
            rules={[
              {
                required: true,
                message: "Please input your password!",
              },
            ]}
            hasFeedback
          >
            <Input.Password />
          </Form.Item>

          <Form.Item
            name="confirm"
            label="Xác thực mật khẩu"
            dependencies={["password"]}
            hasFeedback
            rules={[
              {
                required: true,
                message: "Please confirm your password!",
              },
              ({ getFieldValue }) => ({
                validator(_, value) {
                  if (!value || getFieldValue("password") === value) {
                    return Promise.resolve();
                  }

                  return Promise.reject(
                    new Error(
                      "The two passwords that you entered do not match!"
                    )
                  );
                },
              }),
            ]}
          >
            <Input.Password />
          </Form.Item>

          <Form.Item
            name="agreement"
            valuePropName="checked"
            rules={[
              {
                validator: (_, value) =>
                  value
                    ? Promise.resolve()
                    : Promise.reject(new Error("Should accept agreement")),
              },
            ]}
            {...tailFormItemLayout}
          >
            <Checkbox>
              Tôi đồng ý với <a href="">chính sách của Tiki</a>
            </Checkbox>
          </Form.Item>
          <Form.Item {...tailFormItemLayout}>
            <Button type="primary" htmlType="submit" className="btnDK">
              Đăng ký
            </Button>
            <div style={{ margin: 2 }}>
              <a href="/nhaban/dangNhap">
                Bạn đã có tài khoản. Đăng nhập ngay!
              </a>
            </div>
          </Form.Item>
        </Form>
      </div>
    </Layout>
  );
};

export default DangKy;
