import { Form, Input, Select, Button, DatePicker } from "antd";
import { useState, useEffect } from "react";
import moment from "moment";
import axios from "axios";

const { Option } = Select;

const formItemLayout = {
  labelCol: {
    xs: {
      span: 10,
    },
    sm: {
      span: 8,
    },
  },
  wrapperCol: {
    xs: {
      span: 10,
    },
    sm: {
      span: 16,
    },
  },
};

const tailFormItemLayout = {
  wrapperCol: {
    xs: {
      span: 24,
      offset: 0,
    },
    sm: {
      span: 16,
      offset: 8,
    },
  },
};

const MainProfile = () => {
  const [kh, setKh] = useState(JSON.parse(window.localStorage.getItem("KH")));

  const [form] = Form.useForm();

  const onFinish = (values) => {
    // const listAddress = values.address.split(", ");
    const info = {
      MaKH: kh.MaKH,
      Email: values.email === undefined ? kh.Email : values.Email,
      MatKhau: values.password === undefined ? kh.Password : values.MatKhau,
      Ten: values.name,
      NgaySinh: moment(values.dateOfBirth).format("MM-DD-YYYY"),
      Sdt: values.phone,
      GioiTinh: values.gender === "male" ? "Nam" : "Nữ",
      SoNha: listAddress[0],
      Duong: listAddress[1],
      Phuong: listAddress[2],
      Quan: listAddress[3],
      ThanhPho: listAddress[4],
      TikiXu: 0,
    };
    console.log(info);
  };

  return (
    <div className="register-form">
      <Form
        {...formItemLayout}
        form={form}
        name="register"
        onFinish={onFinish}
        scrollToFirstError
      >
        <Form.Item name="email" label="E-mail">
          <Input disabled={true} defaultValue={kh.Email} />
        </Form.Item>

        <Form.Item name="password" label="Mật khẩu">
          <Input.Password defaultValue={kh.MatKhau} />
        </Form.Item>

        <Form.Item name="name" label="Họ tên">
          <Input defaultValue={kh.Ten} />
        </Form.Item>

        <Form.Item
          name="dateOfBirth"
          label="Ngày sinh"
          tooltip="Ngày sinh của bạn là gì?"
        >
          <DatePicker defaultValue={moment(kh.NgaySinh)} />
        </Form.Item>

        <Form.Item name="phone" label="Số điện thoại">
          <Input
            style={{
              width: "100%",
            }}
            defaultValue={kh.Sdt}
          />
        </Form.Item>

        <Form.Item name="gender" label="Giới tính">
          <Select
            placeholder="Chọn giới tính"
            defaultValue={kh === "Nam" ? "male" : "female"}
          >
            <Option value="male">Nam</Option>
            <Option value="female">Nữ</Option>
          </Select>
        </Form.Item>

        <Form.Item
          name="address"
          label="Địa chỉ"
          tooltip="Nhập địa chỉ theo định dạng: số nhà, đường, phường, quận, thành phố"
        >
          <Input
            defaultValue={
              kh.SoNha.trim() +
              ", " +
              kh.Duong.trim() +
              ", Phường " +
              kh.Phuong.trim() +
              ", Quận " +
              kh.Quan.trim() +
              " " +
              kh.ThanhPho.trim()
            }
          />
        </Form.Item>
        <Form.Item {...tailFormItemLayout}>
          <Button type="primary" htmlType="submit">
            Cập nhật
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default MainProfile;
