import "./style.css";
import React, { useState } from "react";
import {  Form, Layout, Card, Input, Col, Row, Button, Typography } from "antd";

const { Content } = Layout;
const { Title, Text } = Typography;
const { TextArea } = Input;

const onChange = (e) => {
  console.log("Change:", e.target.value);
};
const formItemLayout = {
  labelCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 8,
    },
  },
  wrapperCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 16,
    },
  },
};
const tailFormItemLayout = {
  wrapperCol: {
    xs: {
      span: 24,
      offset: 0,
    },
    sm: {
      span: 16,
      offset: 8,
    },
  },
};

const TaoSP = () => {
  const [form] = Form.useForm();

  const onFinish = (values) => {
    console.log("Received values of form: ", values);
  };
  return (
    <Layout>
      <Content>
        <div className="contentTTNB">
          <div className="site-card-border-less-wrapper">
            <div>
              <Title level={2}>Tạo Sản Phẩm</Title>
            </div>
            <Card bordered={false}>
              <div>
                <Form
                  {...formItemLayout}
                  form={form}
                  onFinish={onFinish}
                  scrollToFirstError
                  wrapperCol={{ span: 10 }}
                >
                  <Form.Item
                    name="masp"
                    label="Mã Sản Phẩm"
                    rules={[{ type: "masp" }, { required: true }]}
                  >
                    <Input defaultValue="26888888" />
                  </Form.Item>

                  <Form.Item
                    name="tensp"
                    label="Tên Sản Phẩm"
                    rules={[{ type: "tensp" }, { required: true }]}
                  >
                    <Input defaultValue="ABC" />
                  </Form.Item>

                  <Form.Item
                    name="slton"
                    label="Số lượng tồn "
                    rules={[{ required: true }]}
                  >
                    <Input defaultValue="149" />
                  </Form.Item>

                  <Form.Item
                    name="loaisp"
                    label="Loại sản phẩm"
                    rules={[{ required: true }]}
                  >
                    <Input defaultValue="Nguyễn Trọng Tuyển" />
                  </Form.Item>

                  <Form.Item
                    name="phuong"
                    label="Phường"
                    rules={[{ required: true }]}
                  >
                    <Input defaultValue="8" />
                  </Form.Item>

                  <Form.Item
                    name="quan"
                    label="Quận"
                    rules={[{ required: true }]}
                  >
                    <Input defaultValue="Phú Nhuận" />
                  </Form.Item>

                  <Form.Item
                    name="thanhpho"
                    label="Thành Phố"
                    rules={[{ required: true }]}
                  >
                    <Input defaultValue="Hồ Chí Minh" />
                  </Form.Item>
                </Form>
              </div>

              <Input.Group size="medium">
                <Row>
                  <Col span={5}>
                    <div className="bordertextTTNB">
                      <Text className="textTTNB">Mã Sản Phẩm </Text>
                      <br />
                    </div>
                    <div className="bordertextTTNB">
                      <Text className="textTTNB">Tên Sản Phẩm </Text>
                      <br />
                    </div>
                    <div className="bordertextTTNB">
                      <Text className="textTTNB">Số lượng tồn </Text>
                      <br />
                    </div>
                    <div className="bordertextTTNB">
                      <Text className="textTTNB">Loại sản phẩm</Text>
                      <br />
                    </div>
                    <div className="bordertextTTNB">
                      <Text className="textTTNB">Giá giảm </Text>
                      <br />
                    </div>
                    <div className="bordertextTTNB">
                      <Text className="textTTNB">Giá bán </Text>
                      <br />
                    </div>
                    <div className="bordertextTTNB">
                      <Text className="textTTNB">Tổng tiền </Text>
                      <br />
                    </div>
                    <div className="bordertextTTNB">
                      <Text className="textTTNB">Thành tiền </Text>
                      <br />
                    </div>
                    <div className="bordertextTTNB">
                      <Text className="textTTNB">Hoa hồng </Text>
                      <br />
                    </div>
                    <div className="bordertextTTNB">
                      <Text className="textTTNB">Mô tả </Text>
                      <br />
                    </div>
                  </Col>
                  <Col span={19}>
                    <Input defaultValue="26888888" />
                    <Input defaultValue="ABCDDD" />
                    <Input defaultValue="1" />
                    <Input defaultValue="abc" />
                    <Input defaultValue="0571665464" />
                    <Input defaultValue="149 " />
                    <Input defaultValue="0571665464" />
                    <Input defaultValue=" 0571665464" />
                    <Input defaultValue=" 232" />
                    <TextArea showCount maxLength={100} onChange={onChange} />
                  </Col>
                </Row>
              </Input.Group>
            </Card>
          </div>
          <div>
            <Button type="primary" className="btnCreTaoSP">
              Tạo
            </Button>
          </div>
        </div>
      </Content>
    </Layout>
  );
};
export default TaoSP;
