import TableItem from "./cartComponents/TableItem";
import InfoPanel from "./cartComponents/InfoPanel";
import { Row, Col } from "antd";
import "./Cart.css";
import { useState, useEffect } from "react";
import axios from "axios";

const Cart = () => {
  const [cart, setCart] = useState([]);
  const [bill, setBill] = useState([]);
  const [product, setProduct] = useState([]);

  useEffect(() => {
    let tempBill = [];
    if (window.localStorage.getItem("cart").length !== 0) {
      setCart(JSON.parse(window.localStorage.getItem("cart")));

      for (let i = 0; i < cart.length; i++) {
        let nameItem;
        let price;

        axios
          .get("http://localhost:5000/api/ctsp/" + cart[i].MaSP)
          .then((res) => {
            setProduct(res.data[0]);
            nameItem = res.data[0].TenSP;
            price = res.data[0].ThanhTienSP;
            let temp = {
              key: i,
              nameItem: nameItem,
              price: price,
              amount: cart[i].SoLuong,
              total: price * cart[i].SoLuong,
            };
            tempBill.push(temp);
          })
          .catch((err) => {
            console.log(err);
          });
      }
      console.log(tempBill);
      setBill(tempBill);
    }
  }, []);

  return (
    <div className="wrap-cart">
      <h3>Giỏ hàng</h3>
      <Row>
        <Col flex={10}>
          <TableItem bill={bill} />
        </Col>
        <Col flex={2}></Col>
        <Col flex="300px">
          <InfoPanel />
        </Col>
      </Row>
    </div>
  );
};

export default Cart;
