import { Table, Form, InputNumber, Button, Space, Pagination, DatePicker, Tooltip} from "antd";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { Layout } from "antd";
import { Typography } from "antd";
import "./style.css";
import axios from "axios";
import React, { useState, useEffect } from "react";
import moment from "moment";
import { SearchOutlined } from "@ant-design/icons";

const dateFormat = "YYYY/MM/DD";
const { Title } = Typography;
const { Content } = Layout;
const columns = [
  {
    title: "Mã Lịch Sử Biến Động",
    dataIndex: "MaLSBD",
    key: "MaLSBD",
  },
  {
    title: "Mã Nhân Viên",
    dataIndex: "MaNV",
    key: "MaNV",
  },
  {
    title: "Tên nhân viên",
    key: "Ten",
    dataIndex: "Ten",
  },
  {
    title: "Lương hiện tại",
    key: "Luong",
    dataIndex: "Luong",
  },
  {
    title: "Lương biến động",
    key: "LuongBD",
    dataIndex: "LuongBD",
  },
  {
    title: "Đơn hàng đã giao",
    key: "DonHangDaGiao",
    dataIndex: "DonHangDaGiao",
  }
];


function LuongNVGH() {

  const [listNVGH, setListNVGH] = useState([]);
  const [current, setCurrent] = useState(1);
  const data = listNVGH;
  useEffect(() => {
      axios
        .get("http://localhost:5000/api/NVQT/listLuongNVGH/1")
        .then((res) => {
          setListNVGH(res.data);
        })
        .catch((err) => {
          console.log(err);
        });
    }, []);
  const onFinish = (ngay) => {
  }
  const onChange = (page) => {
      setCurrent(page);
      axios.get("http://localhost:5000/api/NVQT/listLuongNVGH/" + page).then((res) => {
          setListNVGH(res.data);
      });
  };
  return (
    <div>
      <Layout>
        <Content>
          <Title level={2} classname="titlename">
            Thống kê lương Nhân Viên Giao Hàng theo tháng
          </Title>
          <Space>
            <div>
              <Typography>Nhập tháng bạn muốn xem: </Typography>
            </div>
            <Form layout="inline">
              <Form.Item>
                <DatePicker 
                  defaultValue={moment("2019/10/25", dateFormat)}
                  format={dateFormat}>
                </DatePicker>
              </Form.Item>
              <Form.Item>
                  <Tooltip title="search">
                    <Button
                      type="primary"
                      shape="circle"
                      icon={<SearchOutlined />}
                    />
                  </Tooltip>
              </Form.Item>
            </Form>
          </Space>

          <Table columns={columns} dataSource={data} pagination={false} />
        </Content>
      </Layout>

      <div className="paging" style={{ marginLeft: 500 }}>
        <Pagination
        current={current}
        pageSize={10}
        total={10000}
        onChange={onChange}
        showSizeChanger={false}
        />
      </div>
    </div>
  );
}

export default LuongNVGH;
