import {
  Tabs,
  Layout,
  Table,
  Tag,
  Space,
  Col,
  Row,
  Button,
  Tooltip,
} from "antd";
import { DatePicker } from "antd";
import moment from "moment";
import { Typography } from "antd";
import { SearchOutlined } from "@ant-design/icons";
import "./style.css";

const { Content } = Layout;

const { Title } = Typography;

const { RangePicker } = DatePicker;

const dateFormat = "YYYY/MM/DD";
const monthFormat = "YYYY/MM";

const dateFormatList = ["DD/MM/YYYY", "DD/MM/YY"];

const customFormat = (value) => `custom format: ${value.format(dateFormat)}`;

const columns = [
  {
    title: "Mã Sản Phẩm",
    dataIndex: "MaSP",
    key: "MaSP",
    render: (text) => <a>{text}</a>,
  },
  {
    title: "Tên Sản Phẩm",
    dataIndex: "TenSP",
    key: "TenSP",
  },
  {
    title: "Số lượng",
    dataIndex: "SoLug",
    key: "SoLug",
  },
  {
    title: "Tổng tiền",
    key: "TongTien",
    dataIndex: "TongTien",
  },
];

const data = [
  {
    MaSP: "1",
    TenSP: "John Brown",
    SoLug: 32,
    TongTien: "1000000",
  },
  {
    MaSP: "2",
    TenSP: "Jim Green",
    SoLug: 42,
    TongTien: "1000000",
  },
  {
    MaSP: "3",
    TenSP: "Joe Black",
    SoLug: 32,
    TongTien: "1000000",
  },
];
function DoanhThu() {
  return (
    <Layout>
      <Content>
        <div>
          <Row>
            <Col span={16}>
              <Title level={2} classname="titlename">
                Doanh Thu Theo Ngày
              </Title>
            </Col>
            <Col span={8}>
              <Row>
                <Col>
                  <DatePicker
                    defaultValue={moment("2015/01/01", dateFormat)}
                    format={dateFormat}
                  />
                </Col>
                <Col>
                  <Tooltip title="search">
                    <Button
                      type="primary"
                      shape="circle"
                      icon={<SearchOutlined />}
                    />
                  </Tooltip>
                </Col>
              </Row>
            </Col>
          </Row>
        </div>
        <div>
          <Table columns={columns} dataSource={data} />
        </div>
      </Content>
    </Layout>
  );
}
export default DoanhThu;
