import { Form, Input, Select, Button, DatePicker } from "antd";
import { useState, useEffect } from "react";
import moment from "moment";
import axios from "axios";


const { Option } = Select;

const formItemLayout = {
  labelCol: {
    xs: {
      span: 10,
    },
    sm: {
      span: 8,
    },
  },
  wrapperCol: {
    xs: {
      span: 10,
    },
    sm: {
      span: 16,
    },
  },
};

const tailFormItemLayout = {
  wrapperCol: {
    xs: {
      span: 24,
      offset: 0,
    },
    sm: {
      span: 16,
      offset: 8,
    },
  },
};

const MainProfile = () => {
  const [kh, setKh] = useState({})

  useEffect(() => {
    setKh(JSON.parse(window.localStorage.getItem("KH")));
  }, [])

  const [form] = Form.useForm();

  const onFinish = (values) => {
    const listAddress = values.address.split(", ");
    const info = {
      MaKH: kh.MaKH,
      Email: values.email,
      MatKhau: values.password,
      Ten: values.name,
      NgaySinh: moment(values.dateOfBirth).format("MM-DD-YYYY"),
      Sdt: values.phone,
      GioiTinh: values.gender === "male" ? "Nam" : "Nữ",
      SoNha: listAddress[0],
      Duong: listAddress[1],
      Phuong: listAddress[2],
      Quan: listAddress[3],
      ThanhPho: listAddress[4],
      TikiXu: 0,
    };
    console.log(values);

  };

  return (
    <div className="register-form">
      </div>
  );
};

export default MainProfile;
