import { Table, Pagination, Button, Input, Form, Select ,Space, notification, DatePicker } from "antd";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { Layout } from "antd";
import { Typography , Modal} from "antd";
import "./style.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import moment from "moment";

import { EditTwoTone, DeleteTwoTone } from "@ant-design/icons";

const { Option } = Select;
const { Title } = Typography; 
const { Content } = Layout;
const layout = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 },
};

function EditModal(props) {
  const [isModalVisible, setIsModalVisible] = useState(props.visible);
  const [form] = Form.useForm();

  const handleOk = () => {
    let HoTen = form.getFieldValue("name");
    let NgaySinh = form.getFieldValue("ngaySinh");
    let NgayVaoLam = form.getFieldValue("ngayVaoLam");
    let SDT = form.getFieldValue("sdt");
    let GioiTinh = form.getFieldValue("gioiTinh");
    let SoNha = form.getFieldValue("soNha");
    let Duong = form.getFieldValue("duong");
    let Phuong = form.getFieldValue("phuong");
    let Quan = form.getFieldValue("quan");
    let ThanhPho = form.getFieldValue("thanhPho");
    let MatKhau = form.getFieldValue("password");
    let Luong = form.getFieldValue("luong");
    let HeSoLuong = form.getFieldValue("hsl");
    let Email = form.getFieldValue("email");

    const info = {
      HoTen: HoTen === undefined ? props.NhanVien.HoTen : HoTen,
      Email: Email === undefined ? props.NhanVien.Email : Email,
      NgaySinh: moment(NgaySinh === undefined ? props.NhanVien.NgaySinh : NgaySinh).format("MM-DD-YYYY"),
      NgayVaoLam: moment(NgayVaoLam === undefined ? props.NhanVien.NgayVaoLam : NgayVaoLam).format("MM-DD-YYYY"),
      SDT: SDT === undefined ? props.NhanVien.SDT : SDT,
      GioiTinh: GioiTinh === undefined ? props.NhanVien.GioiTinh : GioiTinh,
      SoNha: SoNha === undefined ? props.NhanVien.SoNha : SoNha,
      Duong: Duong === undefined ? props.NhanVien.Duong : Duong,
      Phuong: Phuong === undefined ? props.NhanVien.Phuong : Phuong,
      Quan: Quan === undefined ? props.NhanVien.Quan : Quan,
      ThanhPho: ThanhPho === undefined ? props.NhanVien.ThanhPho : ThanhPho,
      MatKhau: MatKhau === undefined ? props.NhanVien.MatKhau : MatKhau,
      Luong: parseInt(Luong) === undefined ? props.NhanVien.Luong : Luong,
      HeSoLuong: parseFloat(HeSoLuong) === undefined ? props.NhanVien.HeSoLuong : Luong,
    };

    axios
      .put("http://localhost:5000/api/NVQT/EditNVQLK" + props.NhanVien.MaSP,
        JSON.stringify(info),
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      )
      .then((res) => {
        console.log(res.data);
        setIsModalVisible(false);
        props.setVisibleFalse();
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleCancel = () => {
    setIsModalVisible(false);
    props.setVisibleFalse();
  };

  
  return (
    <>
      <Modal
        title="Basic Modal"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Layout>
            <div>
                <Title level="3" align="center">Sửa Nhân Viên Quản Lý Kho</Title>
            </div>
            <div>
            <Form {...layout}>  
                <Form.Item name="name" label="Nhập tên nhân viên" defaultValue={props.NhanVien.HoTen} >
                    <Input />
                </Form.Item>    
                <Form.Item name="email" label="Email" defaultValue={props.NhanVien.Email} >
                    <Input />
                </Form.Item>
                
                <Form.Item
                name="password"
                label="Mật khẩu"
                defaultValue={props.NhanVien.MatKhau}
                rules={[
                    {
                    required: true,
                    message: "Xin vui lòng nhập mật khẩu!",
                    },
                ]}
                hasFeedback
                >
                <Input.Password />
                </Form.Item>

                <Form.Item
                name="confirm"
                label="Confirm Password"
                dependencies={["password"]}
                hasFeedback
                rules={[
                    {
                    required: true,
                    message: "Xin vui lòng nhập lại mật khẩu!",
                    },
                    ({ getFieldValue }) => ({
                    validator(_, value) {
                        if (!value || getFieldValue("password") === value) {
                        return Promise.resolve();
                        }

                        return Promise.reject(
                        new Error("Hai mật khẩu đã nhập không khớp!")
                        );
                    },
                    }),
                ]}
                >
                <Input.Password />
                </Form.Item>

                <Form.Item name="sdt" label="Số điện thoại" defaultValue={props.NhanVien.SDT}>
                    <Input />
                </Form.Item>

                <Form.Item name="ngaySinh" wrapperCol={{span: 8}} label="Ngày sinh" defaultValue >
                    <DatePicker />
                </Form.Item>

                <Form.Item name="ngayVaoLam" wrapperCol={{span: 8}} label="Ngày vào làm" >
                    <DatePicker />
                </Form.Item>

                <Form.Item
                    wrapperCol={{span: 7}}
                    name="gioiTinh"
                    label="Giới tính"
                >
                    <Select placeholder="Chọn giới tính">
                        <Option value="Nam">Nam</Option>
                        <Option value="Nữ">Nữ</Option>
                    </Select>
                </Form.Item>

                <Form.Item label="Địa chỉ">
                    <Input.Group compact>
                    <Form.Item
                        name="thanhPho"
                        noStyle
                    >
                        <Input style={{ width: '35%' }} placeholder="Nhập thành phố" />
                    </Form.Item>
                    <Form.Item
                        name="quan"
                        noStyle
                    >
                        <Input style={{ width: '35%' }} placeholder="Nhập quận" />
                    </Form.Item>
                    <Form.Item
                        name="phuong"
                        noStyle
                    >
                        <Input style={{ width: '30%' }} placeholder="Nhập phường" />
                    </Form.Item>
                    <Form.Item
                        name="soNha"
                        noStyle
                    >
                        <Input style={{ width: '50%' }} placeholder="Nhập số nhà" />
                    </Form.Item>
                    <Form.Item
                        name="duong"
                        noStyle
                    >
                        <Input style={{ width: '50%' }} placeholder="Nhập tên đường" />
                    </Form.Item>
                    </Input.Group>
                </Form.Item>

                <Form.Item name="luong" wrapperCol={{span: 3}} label="Lương nhân viên (Đơn vị 1.000đ)" >
                    <Input />
                </Form.Item>

                <Form.Item name="hsl" wrapperCol={{span: 3}} label="Hệ số lương nhân viên" >
                    <Input />
                </Form.Item>
            </Form>
            </div>
        </Layout>  
      </Modal>
    </>
  );
}

function AddModal(props) {
  const [isModalVisible, setIsModalVisible] = useState(props.visible);
  const [form] = Form.useForm();

  const handleOk = () => {
    let HoTen = form.getFieldValue("name");
    let NgaySinh = form.getFieldValue("ngaySinh");
    let NgayVaoLam = form.getFieldValue("ngayVaoLam");
    let SDT = form.getFieldValue("sdt");
    let GioiTinh = form.getFieldValue("gioiTinh");
    let SoNha = form.getFieldValue("soNha");
    let Duong = form.getFieldValue("duong");
    let Phuong = form.getFieldValue("phuong");
    let Quan = form.getFieldValue("quan");
    let ThanhPho = form.getFieldValue("thanhPho");
    let MatKhau = form.getFieldValue("password");
    let Luong = form.getFieldValue("luong");
    let HeSoLuong = form.getFieldValue("hsl");
    let Email = form.getFieldValue("email");

    const info = {
      HoTen: HoTen,
      Email: Email,
      NgaySinh: moment(NgaySinh).format("MM-DD-YYYY"),
      NgayVaoLam: moment(NgayVaoLam).format("MM-DD-YYYY"),
      SDT: SDT,
      GioiTinh: GioiTinh,
      SoNha: SoNha,
      Duong: Duong,
      Phuong: Phuong,
      Quan: Quan,
      ThanhPho: ThanhPho,
      MatKhau: MatKhau,
      Luong: parseInt(Luong),
      HeSoLuong: parseFloat(HeSoLuong),
    };

    axios
      .post("http://localhost:5000/api/NVQT/taoNVQLK", JSON.stringify(info), {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((res) => {
        console.log(res.data);
        setIsModalVisible(false);
        props.setVisibleFalse();
        props.setListNhanVien(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleCancel = () => {
    setIsModalVisible(false);
    props.setVisibleFalse();
  };
  


  return (
    <>
      <Modal
        title="Basic Modal"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Layout>
            <div>
                <Title level="3" align="center">Thêm Nhân Viên Quản Lý Kho</Title>
            </div>
            <div>
            <Form {...layout}>  
                <Form.Item name="name" label="Nhập tên nhân viên" >
                    <Input />
                </Form.Item>    
                <Form.Item name="email" label="Email" >
                    <Input />
                </Form.Item>
                
                <Form.Item
                name="password"
                label="Mật khẩu"
                rules={[
                    {
                    required: true,
                    message: "Xin vui lòng nhập mật khẩu!",
                    },
                ]}
                hasFeedback
                >
                <Input.Password />
                </Form.Item>

                <Form.Item
                name="confirm"
                label="Confirm Password"
                dependencies={["password"]}
                hasFeedback
                rules={[
                    {
                    required: true,
                    message: "Xin vui lòng nhập lại mật khẩu!",
                    },
                    ({ getFieldValue }) => ({
                    validator(_, value) {
                        if (!value || getFieldValue("password") === value) {
                        return Promise.resolve();
                        }

                        return Promise.reject(
                        new Error("Hai mật khẩu đã nhập không khớp!")
                        );
                    },
                    }),
                ]}
                >
                <Input.Password />
                </Form.Item>

                <Form.Item name="sdt" label="Số điện thoại" >
                    <Input />
                </Form.Item>

                <Form.Item name="ngaySinh" wrapperCol={{span: 8}} label="Ngày sinh" >
                    <DatePicker />
                </Form.Item>

                <Form.Item name="ngayVaoLam" wrapperCol={{span: 8}} label="Ngày vào làm" >
                    <DatePicker />
                </Form.Item>

                <Form.Item
                    wrapperCol={{span: 7}}
                    name="gioiTinh"
                    label="Giới tính"
                >
                    <Select placeholder="Chọn giới tính">
                        <Option value="Nam">Nam</Option>
                        <Option value="Nữ">Nữ</Option>
                    </Select>
                </Form.Item>

                <Form.Item label="Địa chỉ">
                    <Input.Group compact>
                    <Form.Item
                        name="thanhPho"
                        noStyle
                    >
                        <Input style={{ width: '35%' }} placeholder="Nhập thành phố" />
                    </Form.Item>
                    <Form.Item
                        name="quan"
                        noStyle
                    >
                        <Input style={{ width: '35%' }} placeholder="Nhập quận" />
                    </Form.Item>
                    <Form.Item
                        name="phuong"
                        noStyle
                    >
                        <Input style={{ width: '30%' }} placeholder="Nhập phường" />
                    </Form.Item>
                    <Form.Item
                        name="soNha"
                        noStyle
                    >
                        <Input style={{ width: '50%' }} placeholder="Nhập số nhà" />
                    </Form.Item>
                    <Form.Item
                        name="duong"
                        noStyle
                    >
                        <Input style={{ width: '50%' }} placeholder="Nhập tên đường" />
                    </Form.Item>
                    </Input.Group>
                </Form.Item>

                <Form.Item name="luong" wrapperCol={{span: 3}} label="Lương nhân viên (Đơn vị 1.000đ)" >
                    <Input />
                </Form.Item>

                <Form.Item name="hsl" wrapperCol={{span: 3}} label="Hệ số lương nhân viên" >
                    <Input />
                </Form.Item>
            </Form>
            </div>
        </Layout>  
      </Modal>
    </>
  );
}


function NVQLK() {
    const [listNhanVien, setListNhanVien] = useState([]);
    const [NhanVien, setNhanVien] = useState({})
    const [editVisible, setEditVisible] = useState(false);
    const [addVisible, setAddVisible] = useState(false);

    useEffect(() => {
        axios
          .get("http://localhost:5000/api/NVQT/listNVQLK/")
          .then((res) => {
            setListNhanVien(res.data);
            console.log(listNhanVien);
          })
          .catch((err) => {
            console.log(err);
          });

      }, []);

    const onClickEdit = (item) => {
      setNhanVien(item);
      setEditVisible(true);
    };
  
    const onClickAdd = () => {
      setAddVisible(true);
    };

    const onClickDelete = (nv) => {
      axios
        .delete("http://localhost:5000/api/NVQT/xoaNVQLK/" + nv.MaNV)
        .then( () => {
            axios
            .get("http://localhost:5000/api/NVQT/listNVQLK/")
            .then((res) => {
              setListNhanVien(res.data);
              console.log(listNhanVien);
            })
            .catch((err) => {
              console.log(err);
            });
        })
        .catch ((error) => {console.log(error)});
    };
    

    const columns = [
      {
        title: "Mã Nhân Viên",
        dataIndex: "MaNV",
        key: "MaNV",
      },
      {
        title: "Họ Tên",
        key: "HoTen",
        dataIndex: "HoTen",
      },
      {
        title: "Ngày sinh",
        dataIndex: "NgaySinh",
        key: "NgaySinh",
        render: (_,record) => {return moment(record.NgaySinh).format("YYYY-MM-DD")}
      },
      {
        title: "Địa chỉ",
        key: "DiaChi",
        dataIndex: "SoNha",
        render: (_,record) => {return <div>{record.SoNha + " " +record.Duong + " Phường " + record.Phuong + " Quận " + record.Quan + " " +record.ThanhPho}</div>}
      },
      {
        title: "Email",
        key: "Email",
        dataIndex: "Email",
      },
      {
        title: "SĐT",
        key: "SDT",
        dataIndex: "SDT",
      },
      {
        title: "Giới tính",
        key: "GioiTinh",
        dataIndex: "GioiTinh",
      },
      {
        title: "Ngày Vào Làm",
        key: "NgayVaoLam",
        dataIndex: "NgayVaoLam",
        render: (_,record) => {return moment(record.NgayVaoLam).format("MM-DD-YYYY")}
      },
      {
        title: "Lương",
        key: "Luong",
        dataIndex: "Luong",
      },
      {
        title: "Hệ Số Lương",
        key: "HeSoLuong",
        dataIndex: "HeSoLuong",
      },
      {
        title: "Hành động",
        key: "action",
        render: (_, record) => (
          <Space size="middle">
            <EditTwoTone
              className="edit-button"
              onClick={() => onClickEdit(record)}
            />
            <DeleteTwoTone
              className="delete"
              onClick={() => onClickDelete(record)}
            />
          </Space>
        ),
      },
    ];
    
    const setVisibleFalse = () => {
      setEditVisible(false);
      setAddVisible(false);
    };

  return (
    <div>
        <div>
      <Layout>
        <Content>
          <Title level={2} classname="titlename">
            Danh sách Nhân Viên Quản Lý Kho
          </Title>
          <Button onClick={onClickAdd} >Thêm nhân viên</Button>
          <Table columns={columns} dataSource={listNhanVien} pagination={false} />
        </Content>
      </Layout>
    </div>

      <EditModal
          key={editVisible}
          visible={editVisible}
          NhanVien={NhanVien}
          setVisibleFalse={setVisibleFalse}
        />
        <AddModal
          key={addVisible}
          visible={addVisible}
          setVisibleFalse={setVisibleFalse}
        />
    </div>
  );
}

export default NVQLK;
