import { Card, Col, Row, Pagination } from "antd";
import Badge from "./productComponents/Badge";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

const { Meta } = Card;

const Products = () => {
  const [products, setProducts] = useState([]);
  const [current, setCurrent] = useState(1);

  useEffect(() => {
    axios.get("http://localhost:5000/api/sanpham/1").then((res) => {
      setProducts(res.data);
    });
  }, []);

  const onChange = (page) => {
    setCurrent(page);
    axios.get("http://localhost:5000/api/sanpham/1").then((res) => {
      setProducts(res.data);
    });
  };

  return (
    <div>
      <div className="site-card-wrapper">
        <Row>
          <Col flex={1}>
            <Link to="/productdetail">
              <Card
                hoverable
                style={{ width: 240 }}
                cover={
                  <img
                    alt="example"
                    src="https://salt.tikicdn.com/cache/w200/ts/product/25/fd/0b/b3c2299af08efca753341bafb7d7b627.jpg"
                  />
                }
              >
                <Badge />
              </Card>
            </Link>
          </Col>
          <Col flex={1}>
            <Link to="/productdetail">
              <Card
                hoverable
                style={{ width: 240 }}
                cover={
                  <img
                    alt="example"
                    src="https://salt.tikicdn.com/cache/w200/ts/product/25/fd/0b/b3c2299af08efca753341bafb7d7b627.jpg"
                  />
                }
              >
                <Badge />
              </Card>
            </Link>
          </Col>
          <Col flex={1}>
            <Link to="/productdetail">
              <Card
                hoverable
                style={{ width: 240 }}
                cover={
                  <img
                    alt="example"
                    src="https://salt.tikicdn.com/cache/w200/ts/product/25/fd/0b/b3c2299af08efca753341bafb7d7b627.jpg"
                  />
                }
              >
                <Badge />
              </Card>
            </Link>
          </Col>
          <Col flex={1}>
            <Link to="/productdetail">
              <Card
                hoverable
                style={{ width: 240 }}
                cover={
                  <img
                    alt="example"
                    src="https://salt.tikicdn.com/cache/w200/ts/product/25/fd/0b/b3c2299af08efca753341bafb7d7b627.jpg"
                  />
                }
              >
                <Badge />
              </Card>
            </Link>
          </Col>
          <Col flex={1}>
            <Link to="/productdetail">
              <Card
                hoverable
                style={{ width: 240 }}
                cover={
                  <img
                    alt="example"
                    src="https://salt.tikicdn.com/cache/w200/ts/product/25/fd/0b/b3c2299af08efca753341bafb7d7b627.jpg"
                  />
                }
              >
                <Badge />
              </Card>
            </Link>
          </Col>
        </Row>
      </div>
      <div className="paging" style={{ marginLeft: 500 }}>
        <Pagination defaultCurrent={1} pageSize={30} total={10000} onChange={onChange}/>
      </div>
    </div>
  );
};

export default Products;
