import { useState, useEffect } from "react";
import { Table, Modal, Button, Input, Divider } from "antd";
import axios from "axios";
import moment from "moment";
import { FaStar } from "react-icons/fa";
import "./Bill.css";

function ReviewModal() {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [rating, setRating] = useState(null);
  const [hover, setHover] = useState(null);

  const { TextArea } = Input;

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    console.log(rating);
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <div>
      <Button type="primary" onClick={showModal}>
        Open Modal
      </Button>
      <Modal
        title="Đánh giá"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <div>
          {[...Array(5)].map((star, i) => {
            const ratingValue = i + 1;
            return (
              <label>
                <input
                  type="radio"
                  name="rating"
                  value={ratingValue}
                  onClick={() => setRating(ratingValue)}
                />
                <FaStar
                  className="star"
                  color={
                    ratingValue <= (hover || rating) ? "#ffc107" : "#e4e5e9"
                  }
                  size={50}
                  onMouseEnter={() => setHover(ratingValue)}
                  onMouseLeave={() => setHover(null)}
                />
              </label>
            );
          })}
        </div>
        <TextArea rows={4} placeholder="Vì sao bạn thích sản phẩm" />
      </Modal>
    </div>
  );
}

function CTHDModal({ MaHD, isOpen, setIsOpen }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/cthd/" + MaHD)
      .then((res) => {
        setProducts(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const handleOk = () => {
    setIsOpen(false);
  };

  const handleCancel = () => {
    setIsOpen(false);
  };

  return (
    <div>
      <Modal
        title="Đánh giá"
        visible={isOpen}
        onOk={handleOk}
        onCancel={handleCancel}
      ></Modal>
    </div>
  );
}

const Bill = () => {
  const [maHD, setMaHD] = useState(-1);
  const [hoaDon, setHoaDon] = useState([]);
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    const id = JSON.parse(window.localStorage.getItem("KH")).MaKH;
    axios
      .get("http://localhost:5000/api/hdkh/" + id)
      .then((res) => {
        console.log(res.data);
        setHoaDon(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const columns = [
    {
      title: "Mã hóa đơn",
      dataIndex: "MaHD",
      key: "MaHD",
      render: (text) => {
        return (
          <Button>{text}</Button>
        );
      },
    },
    {
      title: "Ngày mua",
      dataIndex: "NgayLap",
      key: "NgayLap",
      render: (text) => {
        return <div>{moment(text).format("DD/MM/YYYY")}</div>;
      },
    },
    {
      title: "Tổng tiền",
      dataIndex: "TongTien",
      key: "TongTien",
      render: (text) => <div>{text}.000 Đồng</div>,
    },
    {
      title: "Phí vận chuyển",
      dataIndex: "PhiVC",
      key: "PhiVC",
      render: (text) => <div>{text}.000 Đồng</div>,
    },
    {
      title: "Thành tiền",
      dataIndex: "ThanhTienHD",
      key: "ThanhTienHD",
      render: (text) => <div>{text}.000 Đồng</div>,
    },
    {
      title: "Trạng thái đơn hàng",
      key: "TinhTrang",
      dataIndex: "TinhTrang",
    },
  ];

  return (
    <div>
      <Table columns={columns} dataSource={hoaDon} />
      <CTHDModal isOpen={isOpen} setIsOpen={setIsOpen} />
      <Divider />
      <h2>Sản phẩm bạn đã mua</h2>
      <ReviewModal />
    </div>
  );
};

export default Bill;
