import { Table, Form, InputNumber, Button, Space, Pagination} from "antd";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { Layout } from "antd";
import { Typography } from "antd";
import "./style.css";
const { Title } = Typography;
const { Content } = Layout;
const layout = {
  labelCol: { span: 8 },
  wrapperCol: { span: 10 },
};


const columns = [
  {
    title: "Mã Nhân Viên",
    dataIndex: "MaNV",
    key: "MaNV",
    render: (text) => {
      return (
          <Link to="/ChiTietNhanVien">{text}</Link>
      );
    },
  },
  {
    title: "Tên nhân viên",
    key: "TenNV",
    dataIndex: "TenNV",
  },
  {
    title: "Ngày biến động",
    dataIndex: "NgayBD",
    key: "NgayBD",
  },
  {
    title: "Lương hiện tại",
    key: "LuongHT",
    dataIndex: "LuongHT",
  },
  {
    title: "Ghi chú",
    key: "GhiChu",
    dataIndex: "GhiChu",
  },
];

const data = [
  {
    MaNV: "NV000000001",
    TenNV: "Lương Long Hà",
    NgayBD: "14/07/2021",
    LuongHT: "20000000",
    GhiChu: "Self Destruc activated",
  },
  {
    MaNV: "NV000000002",
    TenNV: "Nguyễn Trọng Khang",
    NgayBD: "09/01/2021",
    LuongHT: "30000000",
    GhiChu: "It's High Noonnnnn",
  },
  {
    MaNV: "NV000000003",
    TenNV: "Trần Khánh Gia Uy",
    NgayBD: "23/08/2020",
    LuongHT: "40000000",
    GhiChu: "An anomaly!",
  },
  {
    MaNV: "NV000000004",
    TenNV: "Đỗ Minh Khoa",
    NgayBD: "02/09/2021",
    LuongHT: "40000000",
    GhiChu: "Beep boop beep boop",
  },
];

function LuongNVGH() {
  const onFinish = (values) => {
      const month = values;
  }

  const [listNVGH, setListNVGH] = useState([]);
  const [current, setCurrent] = useState(1);
  const data = listKhachHang;
  useEffect(() => {
      axios
        .get("http://localhost:5000/api/NVQT/listKH/1")
        .then((res) => {
          setListNVGH(res.data);
        })
        .catch((err) => {
          console.log(err);
        });
    }, []);

  const onChange = (page,month) => {
      setCurrent(page);
      axios.get("http://localhost:5000/api/NVQT/listKH/" + page).then((res) => {
          setListNvGH(res.data);
      });
  };
  return (
    <div>
      <Layout>
        <Content>
          <Title level={2} classname="titlename">
            Thống kê lương Nhân Viên Giao Hàng theo tháng
          </Title>
          <Space>
            <div>
              <Typography>Nhập tháng bạn muốn xem: </Typography>
            </div>
            <Form onFinish={onFinish} layout="inline">
              <Form.Item>
                <InputNumber min={1} max={12}></InputNumber>
              </Form.Item>
              <Form.Item>
                <Button type="primary" htmlType="submit" size="middle" >
                      Xác nhận
                </Button>
              </Form.Item>
            </Form>
          </Space>

          <Table columns={columns} dataSource={data} pagination={false} />
        </Content>
      </Layout>

      <div className="paging" style={{ marginLeft: 500 }}>
        <Pagination
        current={current}
        pageSize={10}
        total={10000}
        onChange={onChange}
        showSizeChanger={false}
        />
      </div>
    </div>
  );
}

export default LuongNVGH;
