import { Column } from "@ant-design/charts";
import React, { useState, useEffect } from "react";
import axios from "axios";
const Chart = ({ config, data, setData, setConfig }) => {
  useEffect(() => {
    const dataW = JSON.parse(window.localStorage.getItem("auth"));
    const fetchData = async () => {
      axios
        .get("http://localhost:5000/api/nhaban/doanhthu/" + dataW.MaNhaBan)
        .then((res) => {
          console.log(res.data);
          setData([
            {
              name: "Đơn hàng",
              date: "Jan.",
              money: res.data[0].ThangMot,
            },
            {
              name: "Đơn hàng",
              date: "Feb.",
              money: res.data[0].ThangHai,
            },
            {
              name: "Đơn hàng",
              date: "Mar.",
              money: res.data[0].ThangBa,
            },
            {
              name: "Đơn hàng",
              date: "Apr.",
              money: res.data[0].Thang,
            },
            {
              name: "Đơn hàng",
              date: "May",
              money: res.data[0].Thang,
            },
            {
              name: "Đơn hàng",
              date: "Jun.",
              money: res.data[0].Thang,
            },
            {
              name: "Đơn hàng",
              date: "Jul.",
              money: res.data[0].Thang,
            },
            {
              name: "Đơn hàng",
              date: "Aug.",
              money: res.data[0].Thang,
            },
            {
              name: "Đơn hàng",
              date: "Sep.",
              money: res.data[0].Thang,
            },
            {
              name: "Đơn hàng",
              date: "Oct.",
              money: res.data[0].Thang,
            },
            {
              name: "Đơn hàng",
              date: "Nov.",
              money: res.data[0].Thang,
            },
            {
              name: "Đơn hàng",
              date: "Dec.",
              money: 0,
            },
          ]);
        });


      await setConfig({
        data: data,
        isGroup: true,
        xField: "date",
        yField: "money",
        seriesField: "name",
        dodgePadding: 2,
        label: {
          position: "middle",
          layout: [
            { type: "interval-adjust-position" },
            { type: "interval-hide-overlap" },
            { type: "adjust-color" },
          ],
        },
      });
    };
    fetchData();
    console.log(config);
  }, []);
  
  return (
    <div>
      {/* <Column {...config} /> */}
    </div>
  );
};
export default Chart;
