function NVQLK() {
    const [listNhanVien, setListNhanVien] = useState([]);
    const [current, setCurrent] = useState(1);
    const [amountNV, setAmountNV] = useState(0);
    const [NhanVien, setNhanVien] = useState({})
    const [editVisible, setEditVisible] = useState(false);
    const [addVisible, setAddVisible] = useState(false);

    useEffect(() => {
        axios
          .get("http://localhost:5000/api/NVQT/listNVQLK/1")
          .then((res) => {
            setListNhanVien(res.data);
          })
          .catch((err) => {
            console.log(err);
          });

        axios
          .get("http://localhost:5000/api/SLNVQLK")
          .then((res) => {
            setAmountNV(res.data[0].SL);
            console.log(res.data);
          })
          .catch((err) => {
            console.log(err);
          })
      }, []);

    const onClickEdit = (item) => {
      setNhanVien(item);
      setEditVisible(true);
    };
  
    const onClickAdd = () => {
      setAddVisible(true);
    };
  
    const onClickDelete = (item) => {
      axios
        .delete(
          "xoaNVQLK/:MaNV/" +
            item.id
        )
        .then((response) => {
          console.log(response);
        });
    };

    const onChange = (page) => {
        setCurrent(page);
        axios.get("http://localhost:5000/api/NVQT/listNVQLK/" + page).then((res) => {
            setListNhanVien(res.data);
        });
    };
    
    const columns = [
      {
        title: "Mã Nhân Viên",
        dataIndex: "MaNV",
        key: "MaNV",
      },
      {
        title: "Họ Tên",
        key: "HoTen",
        dataIndex: "HoTen",
      },
      {
        title: "Ngày sinh",
        dataIndex: "NgaySinh",
        key: "NgaySinh",
        render: (_,record) => {return moment(record.NgaySinh).format("MM-DD-YYYY")}
      },
      {
        title: "Địa chỉ",
        key: "DiaChi",
        dataIndex: "SoNha",
        render: (_,record) => {return <div>{record.SoNha + " " +record.Duong + " Phường " + record.Phuong + " Quận " + record.Quan + " " +record.ThanhPho}</div>}
      },
      {
        title: "Email",
        key: "Email",
        dataIndex: "Email",
      },
      {
        title: "SĐT",
        key: "SDT",
        dataIndex: "SDT",
      },
      {
        title: "Giới tính",
        key: "GioiTinh",
        dataIndex: "GioiTinh",
      },
      {
        title: "Ngày Vào Làm",
        key: "NgayVaoLam",
        dataIndex: "NgayVaoLam",
        render: (_,record) => {return moment(record.NgayVaoLam).format("MM-DD-YYYY")}
      },
      {
        title: "Lương",
        key: "Luong",
        dataIndex: "Luong",
      },
      {
        title: "Hệ Số Lương",
        key: "HeSoLuong",
        dataIndex: "HeSoLuong",
      },
      {
        title: "Hành động",
        key: "action",
        render: (text, record) => (
          <Space size="middle">
            <EditTwoTone
              className="edit-button"
              onClick={() => onClickEdit(record)}
            />
            <DeleteTwoTone
              className="delete"
              onClick={() => onClickDelete(record)}
            />
          </Space>
        ),
      },
    ];

  return (
    <div>
        <div>
      <Layout>
        <Content>
          <Title level={2} classname="titlename">
            Danh sách Nhân Viên Quản Lý Kho
          </Title>
          <Button type ="primary" align="right" onClick={onClickAdd} >Thêm nhân viên</Button>
          <Table columns={columns} dataSource={listNhanVien} pagination={false} />
        </Content>
      </Layout>
    </div>

    <div className="paging" style={{ marginLeft: 500 }}>
        <Pagination
        current={current}
        pageSize={10}
        total={amountNV}
        onChange={onChange}
        showSizeChanger={false}
        />
    </div>
    <EditModal
        key={editVisible}
        visible={editVisible}
        NhanVien={NhanVien}
        setVisibleFalse={setVisibleFalse}
      />
      <AddModal
        key={addVisible}
        visible={addVisible}
        setVisibleFalse={setVisibleFalse}
      />
    </div>
  );
}

export default NVQLK;
