import { Typography, Row, Col } from "antd";
import React, { useState } from "react";
import { Modal, Button } from "antd";
import TaoSP from "./taoSP";

import { Table, Tag, Space } from 'antd';
const { Title } = Typography;
const columns = [
  {
    title: 'Name',
    dataIndex: 'name',
    key: 'name',
    render: text => <a>{text}</a>,
  },
  {
    title: 'Age',
    dataIndex: 'age',
    key: 'age',
  },
  {
    title: 'Address',
    dataIndex: 'address',
    key: 'address',
  },
  {
    title: 'Tags',
    key: 'tags',
    dataIndex: 'tags',
    render: tags => (
      <>
        {tags.map(tag => {
          let color = tag.length > 5 ? 'geekblue' : 'green';
          if (tag === 'loser') {
            color = 'volcano';
          }
          return (
            <Tag color={color} key={tag}>
              {tag.toUpperCase()}
            </Tag>
          );
        })}
      </>
    ),
  },
  {
    title: 'Action',
    key: 'action',
    render: (text, record) => (
      <Space size="middle">
        <a>Invite {record.name}</a>
        <a>Delete</a>
      </Space>
    ),
  },
];

const data = [
  {
    key: '1',
    name: 'John Brown',
    age: 32,
    address: 'New York No. 1 Lake Park',
    tags: ['nice', 'developer'],
  },
  {
    key: '2',
    name: 'Jim Green',
    age: 42,
    address: 'London No. 1 Lake Park',
    tags: ['loser'],
  },
  {
    key: '3',
    name: 'Joe Black',
    age: 32,
    address: 'Sidney No. 1 Lake Park',
    tags: ['cool', 'teacher'],
  },
];

const DoanhThu = (props) => {
  const [visible, setVisible] = useState(false);
  return (
    <div>
      <div>
        <Row>
          <Col>
            <Title level={2}>Danh SÃ¡ch Sáº£n Pháº©m</Title>
          </Col>
          <Col>
            <Button
              type="primary"
              className="btnCreateDSSP"
              onClick={() => setVisible(true)}
            >
              Táº¡o Sáº£n Pháº©m
            </Button>
            <Modal
              title="Táº¡o sáº£n pháº©m"
              centered
              visible={visible}
              onOk={() => setVisible(false)}
              onCancel={() => setVisible(false)}
              width={1000}
            >
              <TaoSP />
            </Modal>
          </Col>
        </Row>
      </div>
      <div>
      <Table columns={columns} dataSource={data} />
      </div>
      <div></div>
    </div>
  );
};

export default DoanhThu;
