        </Form.Item>
        <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
            <Button type="primary" htmlType="button" onClick={deletenvgh}>
              Delete 
            </Button>
        </Form.Item>
      </Form>
      </div>
    );
  }
  
  export default ChiTietNV;
