import { Form, Input, InputNumber, Button, Modal, Col, Row, Tooltip, Table, notification, Layout, Title } from 'antd';
import { SearchOutlined } from "@ant-design/icons";
import React, { useState, useEffect } from "react";
import axios from "axios";

const DT = () => {
    const [doanhThu,setDoanhThu] = useState([]);
    
    const onClick = () => {
        let NamLap = form.getFieldValue("NamLap")
        axios
          .get("http://localhost:5000/api/NVQT/DTTT/" + NamLap)
          .then((res) => {
              setDoanhThu(res.data);
          })
          .catch((err) => {
            console.log(err);
          });
  
      };
      const columns = [
        {
          title: "Tháng Một",
          dataIndex: "ThangMot",
          key: "ThangMot",
        },
        {
            title: "Tháng Hai",
            dataIndex: "ThangHai",
            key: "ThangHai",
        },
        {
            title: "Tháng Ba",
            dataIndex: "ThangBa",
            key: "ThangBa",
        },
        {
            title: "Tháng Tư",
            dataIndex: "ThangTu",
            key: "ThangTu",
        },
        {
            title: "Tháng Năm",
            dataIndex: "ThangNam",
            key: "ThangNam",
        },
        {
            title: "Tháng Sáu",
            dataIndex: "ThangSau",
            key: "ThangSau",
        },
        {
            title: "Tháng Bảy",
            dataIndex: "ThangBay",
            key: "ThangBay",
        },
        {
            title: "Tháng Tám",
            dataIndex: "ThangTam",
            key: "ThangTam",
        },
        {
            title: "Tháng Chín",
            dataIndex: "ThangChin",
            key: "ThangChin",
        },
        {
            title: "Tháng Mười",
            dataIndex: "ThangMuoi",
            key: "ThangMuoi",
        },
        {
            title: "Tháng Mười Một",
            dataIndex: "ThangMuoiMot",
            key: "ThangMuoiMot",
        },
        {
            title: "Tháng 12",
            dataIndex: "ThangMuoiHai",
            key: "ThangMuoiHai",
        },

        ]
    return (
        <Layout>
              <div>
                  <Title level="3" align="center">Doanh thu theo năm</Title>
              </div>
              <div>
              <Form {...layout} form={form} layout="horizontal"> 
              <Input.Group compact>
                  <Form.Item name="NamLap" label="Chọn năm">
                      <inputNumber/>
                    </Form.Item>
                  <Form.Item>
                    <Button
                        type="primary"
                        shape="circle"
                        icon={<SearchOutlined />}
                        onClick={onClick}
                      />
                  </Form.Item>
              </Input.Group>
              </Form>
              </div>

              <Content>
                <Table columns={columns} dataSource={doanhThu} pagination={false} />
              </Content>
        </Layout>
    );
}

export default DT;
