import { Layout, Card } from "antd";
import React, { useState, useEffect } from "react";
import { BrowserRouter as Link } from "react-router-dom";
import { Column } from "@ant-design/charts";
import DoanhThu from "./doanhThu";
import axios from "axios";
const { Content } = Layout;

const TrangChu = () => {
  const [data, setData] = useState([
    {
      name: "Đơn hàng",
      date: "Jan.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Feb.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Mar.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Apr.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "May",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Jun.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Jul.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Aug.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Sep.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Oct.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Nov.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Dec.",
      money: 0,
    },
  ]);

  let dataW = JSON.parse(window.localStorage.getItem("auth"));

  useEffect(() => {
    for (let i = 1; i <= 12; i++) {
      const info1 = {
        MaNhaBan: dataW.MaNhaBan,
        thang: i,
      };
      axios
        .post("http://localhost:5000/api/nhaban/thang", JSON.stringify(info1), {
          headers: { "Content-Type": "application/json" },
        })
        .then((res) => {
          const money = res.data[0].donhang;
          const temp = data;
          temp[i-1] = { ...temp[i-1], money };
          setData(temp);
          console.log(temp);
        })
        .catch((err) => {
          console.log(err);
        });
    }
  }, [data]);

  const config = {
    isGroup: true,
    xField: "date",
    yField: "money",
    seriesField: "name",
    dodgePadding: 2,
    label: {
      position: "middle",
      layout: [
        { type: "interval-adjust-position" },
        { type: "interval-hide-overlap" },
        { type: "adjust-color" },
      ],
    },
  };
  return (
    <Layout>
      <Content>
        <Card
          title="Trang Chủ"
          title="Hiệu quả hoạt động theo tháng"
          extra={<Link to="/nhaban/doanhThu">Xem chi tiết</Link>}
        >
          <Column {...config}  />
        </Card>
      </Content>
    </Layout>
  );
};
export default TrangChu;
