import { Layout, Card } from "antd";
import React, { useState, useEffect } from "react";
import { BrowserRouter as Link } from "react-router-dom";
import { Column } from "@ant-design/charts";
import DoanhThu from "./doanhThu";
import axios from "axios";
const { Content } = Layout;

const TrangChu = () => {
  const [data, setData] = useState([
    {
      name: "Đơn hàng",
      date: "Jan.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Feb.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Mar.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Apr.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "May",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Jun.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Jul.",
      money: 0,
    },
    {
      name: "Đơn hàng",
      date: "Aug.",
      money: 0,
    },
  ]);

  let dataW = JSON.parse(window.localStorage.getItem("auth"));

  useEffect(() => {
    
    const info = {
      MaNhaBan: dataW.MaNhaBan,
      thang: 1,
    };
    axios
      .post("http://localhost:5000/api/nhaban/thang", JSON.stringify(info), {
        headers: { "Content-Type": "application/json" },
      })
      .then((res) => {
        const money = res.data[0].donhang;
        const temp = data;
        temp[0] = { ...temp[0], money };
        setData(temp);
        console.log(temp);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const config = {
    data: data,
    isGroup: true,
    xField: "date",
    yField: "money",
    seriesField: "name",
    dodgePadding: 2,
    label: {
      position: "middle",
      layout: [
        { type: "interval-adjust-position" },
        { type: "interval-hide-overlap" },
        { type: "adjust-color" },
      ],
    },
  };
  return (
    <Layout>
      <Content>
        <Card
          title="Trang Chủ"
          title="Hiệu quả hoạt động theo tháng"
          extra={<Link to="/nhaban/doanhThu">Xem chi tiết</Link>}
        >
          <Column {...config} />
        </Card>
      </Content>
    </Layout>
  );
};
export default TrangChu;
