import {useState, useEffect} from "react";
import { Form, Input, Checkbox, Button, Col } from "antd";
import { Layout } from 'antd';
import axios from 'axios';


const {Header, Content, Footer} = Layout;

const ChiTietNV = () => {
    const [componentSize, setComponentSize] = useState('default');
    const [ctnv, setCtnv] = useState([]);
    const [form] = Form.useForm()

    useEffect(() => {
      axios
        .get("http://localhost:5000/api/NVQL/ctnv/" + JSON.parse(window.localStorage.getItem("MaNV")))
        .then((res) => {
          console.log(res.data)
          setCtnv(res.data[0])
          console.log(ctnv)
        })
        .catch((err) => {
          console.log(err);
        });
      }, []);

    const onFormLayoutChange = ({ size }) => {
      setComponentSize(size);
    };
  
    const handleOk = () => {
      let HoTen = form.getFieldValue("name");
      let NgaySinh = form.getFieldValue("NgaySinh");
      let NgayVaoLam = form.getFieldValue("NgayVaoLam");
      let SDT = form.getFieldValue("SDT");
      let GioiTinh = form.getFieldValue("GioiTinh");
      let SoNha = form.getFieldValue("SoNha");
      let Duong = form.getFieldValue("Duong");
      let Phuong = form.getFieldValue("Phuong");
      let Quan = form.getFieldValue("Quan");
      let ThanhPho = form.getFieldValue("ThanhPho");
      let MatKhau = form.getFieldValue("MatKhau");
      let Luong = form.getFieldValue("Luong");
      let HeSoLuong = form.getFieldValue("HeSoLuong");
      let Email = form.getFieldValue("Email");
  
      const info = {
        HoTen: HoTen === undefined ? props.NhanVien.HoTen : HoTen,
        Email: Email === undefined ? props.NhanVien.Email : Email,
        NgaySinh: moment(
          NgaySinh === undefined ? props.NhanVien.NgaySinh : NgaySinh
        ).format("MM-DD-YYYY"),
        NgayVaoLam: moment(
          NgayVaoLam === undefined ? props.NhanVien.NgayVaoLam : NgayVaoLam
        ).format("MM-DD-YYYY"),
        SDT: SDT === undefined ? props.NhanVien.SDT : SDT,
        GioiTinh: GioiTinh === undefined ? props.NhanVien.GioiTinh : GioiTinh,
        SoNha: SoNha === undefined ? props.NhanVien.SoNha : SoNha,
        Duong: Duong === undefined ? props.NhanVien.Duong : Duong,
        Phuong: Phuong === undefined ? props.NhanVien.Phuong : Phuong,
        Quan: Quan === undefined ? props.NhanVien.Quan : Quan,
        ThanhPho: ThanhPho === undefined ? props.NhanVien.ThanhPho : ThanhPho,
        MatKhau: MatKhau === undefined ? props.NhanVien.MatKhau : MatKhau,
        Luong:
          Luong === undefined ? parseInt(props.NhanVien.Luong) : parseInt(Luong),
        HeSoLuong:
          HeSoLuong === undefined
            ? parseFloat(props.NhanVien.HeSoLuong)
            : parseFloat(HeSoLuong),
      };
  
      axios
        .put(
          "http://localhost:5000/api/NVQT/EditNVQLK/" + props.NhanVien.MaNV,
          JSON.stringify(info),
          {
            headers: {
              "Content-Type": "application/json",
            },
          }
        )
        .then((res) => {
          axios
            .get("http://localhost:5000/api/NVQT/listNVQLK/")
            .then((res) => {
              props.setListNhanVien(res.data);
            })
            .catch((err) => {
              console.log(err);
            });
  
          console.log(res.data);
          setIsModalVisible(false);
          props.setVisibleFalse();
        })
        .catch((err) => {
          console.log(err);
        });
    };
    
    return (
      <div>

      <Form
        labelCol={{
          span: 4,
        }}
        wrapperCol={{
          span: 14,
        }}
        layout="horizontal"
        initialValues={{
          size: componentSize,
        }}
        onValuesChange={onFormLayoutChange}
        size={componentSize}
        form={form}
      >
        
        <Form.Item label="Mã Nhân viên" name="MaNV">
          <Input defaultValue={ctnv.MaNV}/>
        </Form.Item>
        <Form.Item label="Tên Nhân viên" name="Ten">
          <Input defaultValue={ctnv.Ten}/>
        </Form.Item>
        <Form.Item label="Giới Tính" name="GioiTinh">
          <Input defaultValue={ctnv.GioiTinh}/>
        </Form.Item>
        <Form.Item label="Số điện thoại" name="Sdt">
          <Input defaultValue={ctnv.Sdt}/>
        </Form.Item>
        <Form.Item label="Ngày sinh" name="NgaySinh">
          <Input defaultValue={ctnv.NgaySinh}/>
        </Form.Item>
        <Form.Item label="Số nhà" name="SoNha">
          <Input defaultValue={ctnv.SoNha}/>
        </Form.Item>
        <Form.Item label="Đường" name="Duong">
          <Input defaultValue={ctnv.Duong}/>
        </Form.Item>
        <Form.Item label="Phường" name="Phuong">
          <Input defaultValue={ctnv.Phuong}/>
        </Form.Item>
        <Form.Item label="Quận" name="Quan">
          <Input defaultValue={ctnv.Quan}/>
        </Form.Item>
        <Form.Item label="Thành Phố" name="ThanhPho">
          <Input defaultValue={ctnv.ThanhPho}/>
        </Form.Item>
        <Form.Item label="Email" name="Email">
          <Input defaultValue={ctnv.Email}/>
        </Form.Item>
        <Form.Item label="Mật Khẩu" name="MatKhau">
          <Input defaultValue={ctnv.MatKhau}/>
        </Form.Item>
        <Form.Item label="Ngày Vào Làm" name="NgayVaoLam">
          <Input defaultValue={ctnv.NgayVaolam}/>
        </Form.Item>
        <Form.Item label="Lương" name="Luong">
          <Input defaultValue={ctnv.Luong}/>
        </Form.Item>
        <Form.Item label="Đơn Hàng đã giao" name="DonHangDaGiao">
          <Input defaultValue={ctnv.DonHangDaGiao}/>
        </Form.Item>
        <Form.Item label="Mã người Quản lý" name="MaQL">
          <Input defaultValue={ctnv.MaQL}/>
        </Form.Item>
      </Form>
      </div>
    );
  }
  
  export default ChiTietNV;
