import "./style.css";
import { Layout, Card, Input, Col, Row, Select, Table, Tag, Space } from "antd";
import { Typography } from "antd";

const { Title } = Typography;
const gridStyle = {
  width: "100%",
  textAlign: "center",
};
const { Content } = Layout;
const { Option } = Select;

const columns = [
  {
    title: "Tên Sản Phẩm",
    dataIndex: "TenSP",
    key: "TenSP",
    render: (text) => <a>{text}</a>,
  },
  {
    title: "Số lượng",
    dataIndex: "SoLug",
    key: "SoLug",
  },
  {
    title: "Giá Tiền",
    dataIndex: "address",
    key: "address",
  },
 
];

const data = [
  {
    key: "1",
    name: "John Brown",
    age: 32,
    address: "New York No. 1 Lake Park",
    tags: ["nice", "developer"],
  },
  {
    key: "2",
    name: "Jim Green",
    age: 42,
    address: "London No. 1 Lake Park",
    tags: ["loser"],
  },
  {
    key: "3",
    name: "Joe Black",
    age: 32,
    address: "Sidney No. 1 Lake Park",
    tags: ["cool", "teacher"],
  },
];

const ThongTinHD = () => {
  return (
    <Layout>
      <Content>
        <div className="site-input-group-wrapper">
          <div className="site-card-border-less-wrapper">
            <Card
              title="Thông Tin Hóa Đơn"
              bordered={false}
              style={{ width: 1100 }}
            >
              <Input.Group size="medium">
                <Row gutter={8}>
                  <Col span={5}>
                    <Input defaultValue="Mã Hóa Đơn" />
                    <Input defaultValue="Ngày lập Hóa Đơn" />
                    <Input defaultValue="Tổng tiền" />
                  </Col>
                  <Col span={8}>
                    <Input defaultValue="26888888" />
                    <Input defaultValue="6:30 12/08/2021" />
                    <Input defaultValue="0571" />
                  </Col>
                </Row>
              </Input.Group>
            </Card>
          </div>
          <div>
            <Card
              title="Chi Tiết Hóa Đơn"
              bordered={false}
              style={{ width: 1100 }}
            >
              <Table columns={columns} dataSource={data} />
            </Card>
          </div>
        </div>
      </Content>
    </Layout>
  );
};
export default ThongTinHD;
