import { useState, useEffect } from "react";
import { Table, Modal, Button, Input, Divider } from "antd";
import axios from "axios";
import { FaStar } from "react-icons/fa";
import "./Bill.css";

function ReviewModal() {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [rating, setRating] = useState(null);
  const [hover, setHover] = useState(null);

  const { TextArea } = Input;

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    console.log(rating);
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <div>
      <Button type="primary" onClick={showModal}>
        Open Modal
      </Button>
      <Modal
        title="Đánh giá"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <div>
          {[...Array(5)].map((star, i) => {
            const ratingValue = i + 1;
            return (
              <label>
                <input
                  type="radio"
                  name="rating"
                  value={ratingValue}
                  onClick={() => setRating(ratingValue)}
                />
                <FaStar
                  className="star"
                  color={
                    ratingValue <= (hover || rating) ? "#ffc107" : "#e4e5e9"
                  }
                  size={50}
                  onMouseEnter={() => setHover(ratingValue)}
                  onMouseLeave={() => setHover(null)}
                />
              </label>
            );
          })}
        </div>
        <TextArea rows={4} placeholder="Vì sao bạn thích sản phẩm" />
      </Modal>
    </div>
  );
}

const Bill = () => {
  const [products, setProducts] = useState([]);
  const [hoaDon, setHoaDon] = useState([]);

  useEffect(() => {
    const id = JSON.parse(window.localStorage.getItem("KH")).MaKH;
    setProducts([{ a: "a" }]);
    axios
      .get("http://localhost:5000/api/hdkh/" + id)
      .then((res) => {
        console.log(res.data);
        setHoaDon(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const columns = [
    {
      title: "Mã hóa đơn",
      dataIndex: "MaHD",
      key: "MaHD",
    },
    {
      title: "Ngày mua",
      dataIndex: "NgayLap",
      key: "NgayLap",
    },
    {
      title: "Tổng tiền",
      dataIndex: "TongTien",
      key: "TongTien",
    },
    {
      title: "Phí vận chuyển",
      dataIndex: "PhiVC",
      key: "PhiVC",
    },
    {
      title: "Tổng tiền",
      dataIndex: "ThanhTienHD",
      key: "ThanhTienHD",
    },
    {
      title: "Trạng thái đơn hàng",
      key: "TrangThai",
      dataIndex: "TrangThai",
    },
  ];

  return (
    <div>
      <Table columns={columns} dataSource={hoaDon} />
      <Divider />
      <h2>Sản phẩm bạn đã mua</h2>
      <ReviewModal />
    </div>
  );
};

export default Bill;
