import { Table } from "antd";
import { Layout } from "antd";
import { Typography } from "antd";
import "./style.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import moment from "moment";
const { Title } = Typography;
const { Content } = Layout;


const columns = [
  {
    title: "Mã Kho",
    dataIndex: "MaKho",
    key: "MaKho",
  },
  {
    title: "Tên Khách Hàng",
    key: "Ten",
    dataIndex: "Ten",
  },
  {
    title: "Ngày sinh",
    dataIndex: "NgaySinh",
    key: "NgaySinh",
    render: (_,record) => {return moment(record.NgaySinh).format("MM-DD-YYYY")}
  },
  {
    title: "Địa chỉ",
    key: "DiaChi",
    dataIndex: "SoNha",
    render: (_,record) => {return <div>{record.SoNha + " " +record.Duong + " Phường " + record.Phuong + " Quận " + record.Quan + " " +record.ThanhPho}</div>}
  },
  {
    title: "Email",
    key: "Email",
    dataIndex: "Email",
  },
  {
    title: "SĐT",
    key: "Sdt",
    dataIndex: "Sdt",
  },
  {
    title: "Giới tính",
    key: "GioiTinh",
    dataIndex: "GioiTinh",
  },
  {
    title: "Tiki xu",
    key: "TikiXu",
    dataIndex: "TikiXu",
  },
];



function DSKH() {
    const [listKho, setListKho] = useState([]);

    useEffect(() => {
        axios
          .get("http://localhost:5000/api/NVQT/dsKho/")
          .then((res) => {
            setListKho(res.data);
          })
          .catch((err) => {
            console.log(err);
          });

      }, []);

    
  return (
    <div>
        <div>
      <Layout>
        <Content>
          <Title level={2} classname="titlename">
            Danh sách kho
          </Title>
          <Table columns={columns} dataSource={listKho} pagination={false} />
        </Content>
      </Layout>
    </div>

    </div>
  );
}

export default DSKH;
