import { BrowserRouter as Router, Switch, Route, Link, Redirect } from "react-router-dom";
import "antd/dist/antd.css";
import React from "react";
import { useEffect, useState } from "react";

import Main from "./components/Main.js";

const NVQT = () => {
  const [auth, setAuth] = useState(window.localStorage.getItem("NV"));
  const [verify,setVerify] = useState(<i class="fas fa-bullseye-pointer    "></i>)

  useEffect(() => {
    setAuth(window.localStorage.getItem("NV"));
  }, []);


  return (
    <Router>
      <Switch>
        <Route exact path="/QT/LogIn">
          <DangNhap auth={auth} setAuth={setAuth}  />
        </Route>

        <Route exact path="/QT">
          {auth === null ? (
            <Redirect to="/QT/LogIn" />
          ) : (
            <Main/>
          )}
        </Route>
      </Switch>
    </Router>
  );
};

export default NVQT;
