import "./style.css";
import { BrowserRouter as Link } from "react-router-dom";
import {
  Layout,
  Card,
  Input,
  Col,
  Row,
  Select,
  Table,
  Button,
  Typography,
} from "antd";
import { Form, Space } from "antd";
import React, { useState, useEffect } from "react";
import axios from "axios";
const { Content } = Layout;

function handleChange(value) {
  console.log(`selected ${value}`);
}
const formItemLayout = {
  labelCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 8,
    },
  },
  wrapperCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 16,
    },
  },
};

const ThongTinHD = () => {
  const onFinish = (values) => {};
  const [form] = Form.useForm();
  let dataW = JSON.parse(window.localStorage.getItem("auth"));

  return (
    <Layout>
      <Content>
        <div className="site-input-group-wrapper">
          <Card title="Thông Tin Hóa Đơn" bordered={false}>
            <Form
              form={form}
              {...formItemLayout}
              onFinish={onFinish}
              scrollToFirstError
              wrapperCol={{ span: 10 }}
            >
              <Form.Item name="MaHD" label="Mã Hóa Đơn">
                <Input defaultValue={dataW.TenNhaBan} />
              </Form.Item>

              <Form.Item name="Ten" label="Tên Khách Hàng">
                <Input defaultValue={dataW.TenNhaBan} />
              </Form.Item>

              <Form.Item name="NgayLap" label="Ngày lập">
                <Input defaultValue={dataW.EmailNhaBan} />
              </Form.Item>

              <Form.Item name="TongTien" label="Tổng tiền">
                <Input defaultValue={dataW.SDTNhaBan} />
              </Form.Item>

              <Form.Item name="TinhTrang" label="Tình trạng">
                <Input.Password defaultValue={dataW.MatKhauNhaBan} />
              </Form.Item>

              <Form.Item name="SLTikiXu" label="SL Tiki xu">
                <Input defaultValue={dataW.DiaChiNhaBan} />
              </Form.Item>
              <Form.Item name="PhiVC" label="Phí vận chuyển ">
                <Input.Password defaultValue={dataW.MatKhauNhaBan} />
              </Form.Item>

              <Form.Item name="ThanhTienHD" label="Thành tiền">
                <Input defaultValue={dataW.DiaChiNhaBan} />
              </Form.Item>
              <Form.Item name="MatKhauNhaBan" label="Mật khẩu ">
                <Input.Password defaultValue={dataW.MatKhauNhaBan} />
              </Form.Item>

              <Form.Item name="DiaChiNhaBan" label="Địa chỉ">
                <Input defaultValue={dataW.DiaChiNhaBan} />
              </Form.Item>

              <Form.Item>
                <Button
                  type="primary"
                  htmlType="submit"
                  className="btnSaveTTNB"
                >
                  Lưu
                </Button>
              </Form.Item>
            </Form>
          </Card>
        </div>
      </Content>
    </Layout>
  );
};
export default ThongTinHD;
